css:
@-moz-document domain("youtube.com") {
:root {
	--blur-radius: 15px;
}

#masthead:not(.style-scope)[dark] {
	background-color: rgba(33, 33, 33, 0.85);
	backdrop-filter: blur(var(--blur-radius));
}

#masthead:not(.style-scope) {
	background-color: rgba(255, 255, 255, 0.9);
	backdrop-filter: contrast(1.3) saturate(1.3) blur(var(--blur-radius));
}

.title.ytd-video-primary-info-renderer {
	font-family: "YouTube Sans";
	font-weight: 600;
	font-size: 1.9rem;
}

h2.ytd-simple-menu-header-renderer, #account-name.ytd-active-account-header-renderer {
	font-family: "YouTube Sans";
	font-weight: 600;
	font-size: 1.7rem;
}

#header.ytd-rich-grid-renderer {
	display: none;
}

.ytp-popup {
	backdrop-filter: blur(var(--blur-radius));
	text-shadow: none;
	background: rgba(28,28,28,0.7);
	border-radius: 5px;
}

.html5-video-info-panel {
	background: rgba(28,28,28,0.7);
	backdrop-filter: saturate(1.3) contrast(1.3) blur(25px);
}

#voice-search-button.ytd-masthead {
	display: none;
}

html:not(.style-scope)[dark], :not(.style-scope)[dark] {
	--ytd-searchbox-background: rgba(0, 0, 0, 0.4);
}

html:not(.style-scope), :not(.style-scope) {
	--ytd-searchbox-background: rgba(255, 255, 255, 0.4);
}

ytd-multi-page-menu-renderer {
	backdrop-filter: saturate(2) contrast(2) blur(5px);
}
}
