@namespace url(http://www.w3.org/1999/xhtml);
/* written by Thomas Hartmann, version 0.0.3 */
@-moz-document domain("flickr.com"),
               url("http://flickr.com"),
               url-prefix("http://www.flickr.com/photos")
{
body { color:#555 !important; background-color:#101010 !important; }

#Main, h1 { color:white; background-color:#151515; }
#Main td { color:#CCC !important; background-color:#151515 !important; }
#Main td a { color:#6cf !important; }

a:link { color: #999 !important; }
a:hover { background-color: #333; color: #AAA; }

#photoswftd h1, h4 {color:#6cf;}

div#TopBar, div#FooterWrapper { color:black; background-color:white; }
div#DiscussTopic { color:white !important; }

#candy_nav_button_bar .candy_menu a, #candy_nav_button_bar .candy_menu a:link {
color:#333 !important;}
}

ul.tt-activity li.act-item.hover, ul.tt-activity li.act-item:hover {
background-color:#444444;
}

#SlideShowButton, #ShareButton {
border:1px solid #555;
}
