css:
@-moz-document regexp("https?://www.rockpapershotgun.com/(?!(.*forums)|(.*wp-)).*") {
#page_wrapper
{
    background: #1D1D1D !important;
    padding-bottom: 1em !important;
}

.blog-post .tags:after {
    border-bottom: 1px solid rgb(200, 200, 200) !important;
    width: 100%;
}

.leaderboard_container:not(.sticky)
{
    height: 32px !important;
}


#content_above
{
    padding-right: 0px !important;
}

p.tags
{
    padding-top: 16px;
}

.breaking
{
    background-color: rgb(65, 65, 65) !important;
}

.header-desktop, .footer
{
    background-color: rgb(200, 200, 200) !important;
}

.support-us-promo
{
    background-color: rgb(150, 150, 150) !important;
}

.button
{
    color: rgb(255, 255, 255) !important;
    background-color: rgb(173, 10, 10) !important;
}

.article nav.breadcrumb ul li a, div.container-690 p.title a
{
    color: rgb(0, 0, 0) !important;
}

div.container-690 p.byline a.comment-count,
.article div.details a.comment-count 
{
    color: rgb(0, 0, 200) !important;
}

nav.breadcrumb, article main
{
    border-bottom: 1px solid rgb(200, 200, 200) !important;
}

.article a:not(.button),
p.tags a,
#comments a:not(.button),
div.details ul li a,
div.container-690 a:not(.button),
div.container-690 p.label
{
    color: rgb(193, 39, 44) !important;
}
    
.footer a
{
    color: rgb(0, 0, 0) !important;
}
    
div.details p.subtitle
{
    color: rgb(65, 65, 65) !important;
}

#twitter-widget-0,
#twitter-widget-1,
#twitter-widget-2,
#twitter-widget-3,
#twitter-widget-4,
#twitter-widget-5,
#twitter-widget-6,
#twitter-widget-7,
#twitter-widget-8,
#twitter-widget-9
{
    margin-left: 1%;
}

center #twitter-widget-0,
center #twitter-widget-1,
center #twitter-widget-2,
center #twitter-widget-3,
center #twitter-widget-4,
center #twitter-widget-5,
center #twitter-widget-6,
center #twitter-widget-7,
center #twitter-widget-8,
center #twitter-widget-9
{
    margin-left: 0%;
}

.article main amp-img, .article main img {
    border: 5px solid rgb(206, 206, 206);
}
    
div.image
{
    width: 1060px;
    height: auto !important;
    border: 5px solid rgb(206, 206, 206) !important;
}
    
iframe
{
	width:65% !important;
	margin-left:17%;
}

.embed-wrapper
{
	background-color: rgb(255, 255, 255) !important;
}

.blog-post .icon
{
    left: 5px !important;
    top: 5px !important;
}
    
div.related-games-list
{
    width:auto !important;
}

.ads-enabled .page > main .above
{
    padding-right:0px !important;
}

#comments
{
    width: 100%;
    margin: 0px 0px 0px !important;
	padding-left:21.6% !important;
	border-bottom:none;
}

.page > main, .footer
{
    padding: 0px !important;
}

.article > header, .article > main, div.container-760, div.container-690
{
    max-width: 1060px !important;;
}

div.spotlight,
div.leaderboards,
aside,
div.billboard-container,
#recommendations,
div.have-you-played-widget,
.mormont-recommendations,
.spotlight-container,
div#gn-takeover,
div.sidebar
{
    display:none !important;
}

#RPS_LB_1_1,
#gn-takeover-sticky-wrapper,
.ad-wrapper,
#comments-mpu-container,
div#comments-mpu-first.mpu-container,
div.comments-mpu-container,
div#comments-mpu-last.mpu-container,
#sh-frame,
div.shuttle-wrapper,
div.promo-banner
{
    visibility:hidden !important;
    display:none !important;
}

div#jquery-lightbox
{
    left: -300px !important;
}

#wpadminbar a img.badge
{
    display: none !important;
}

.liveblog {
    margin: 0px 0px 0px !important;
    max-width: 680px !important;
}
}

@-moz-document regexp("https?://www.rockpapershotgun.com/(?!(.*guides)).*") {
div.spotlight-bar, div.bb-layer.bb-commercial-layer, .advert_container
{
    display: none !important;
}

#content
{
    max-width: 60% !important;
}
}

@-moz-document regexp("https?://www.rockpapershotgun.com/") {
div.below
{
    display: none !important;
}

#content
{
    max-width: 75% !important;
}

body[data-version=hd] .featured_tag_shelf .container {
    grid-template-columns: 435px auto 275px !important;
}

.supporters_promo {
    max-width: 175px !important;
}
}

@-moz-document regexp("https?://www.rockpapershotgun.com/(?!(.*forums)|(.*wp-)|(.*supporter-redirect)|(.*supportrps)|(.*support-us)|(.*about)|(.*advertise)|(.*hey)|(.*privacy)|(.*tags)|(.*videos)|(.*hardware)|(.*featured-articles)|(.*pc-game-reviews)|(.*pc-game-news)|(.*tag)|(.*category)).*") {
div.video-hub,
.video-hub, .below > .section-title
{
    display:none !important;
}

.page > main
{
    background: #1D1D1D !important;
}

div.above, div.below
{
    border-top:  3px solid rgb(206, 206, 206) !important;
    border-bottom: 3px solid rgb(250, 250, 250) !important;
    border-left: 2px solid rgb(225, 225, 225) !important;
    border-right: 2px solid rgb(225, 225, 225) !important;
    border-radius: 0.5em !important;
    margin-top: 1em !important;
    background-color: rgb(255, 255, 255) !important;
}
}
