css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userstyles.org") {
* { font-style: normal !important; }
body {
background: #FFF !important;
color: #333 !important;
font: 13px/1.8 Tahoma,sans-serif !important;
}

h1 {
padding: 5px 15px !important;
color: #FFF !important;
font: 24px 'Lucida Sans Unicode', Tahoma !important;
}

a {
padding-bottom: 2px !important;
text-decoration: none !important;
border-bottom: 1px dashed #CCC !important;
}

#install {
background: #73A055 !important;
color: #FFF !important;
font-size: 13px !important;
}
#install a { color: #EEE !important; }
p button {
width: 200px !important;
height: 40px !important;
margin: 0 0 15px !important;
display: inline-block !important;
float: right !important;
border: 0 !important;
}

form { margin: 5px 0 30px !important; }
form * { font-size: 13px !important; }

.screenshot {
padding: 2px !important;
border: 1px solid #EEE !important;
}
.screenshot-caption {
margin: 15px 0 15px !important;
padding: 2px 15px !important;
display: inline-block;
background: #06C !important;
color: #FFF !important;
text-transform: uppercase !important;
-moz-border-radius: 5px;
}

}
