css:
@-moz-document domain(pub.dev) {
  @media screen /*[[USE_DARK_THEME]]*/ {
    :root {
      --textColor: #dddddd;
      --bodyColor: #121212;
      --pane01: #1e1e1e;
      --pane02: #232323;
      --pane03: #252525;
      --pane04: #272727;
      --pane06: #2c2c2c;
      --pane08: #2e2e2e;
      --pane12: #333333;
      --pane16: #363636;
      --pane24: #383838;
      --gray: #848484;
      --darkBlue: #151920;
      --darkRed: #391f1f;
      --activeRed: #ae4b4b;
    }

    body {
      color: var(--textColor);
      background-color: var(--bodyColor);
      line-height: 1.6;
      margin: 0;
      padding: 0;
      overflow-wrap: break-word;
    }

    ::-webkit-scrollbar-thumb {
      background-color: var(--pane12);
    }

    .package-vp-icon {
      filter: invert(1);
    }

    .package-badge,
.feature {
      background: transparent;
    }

    .package-tag.unidentified,
.package-tag.unlisted {
      background: var(--darkRed);
      color: var(--textColor);
    }

    .-pub-tag-badge {
      background: var(--darkBlue);
    }

    .mdc-checkbox .mdc-checkbox__native-control:enabled:not(:checked):not(:indeterminate):not([data-indeterminate=true]) ~ .mdc-checkbox__background {
      border-color: var(--gray);
    }

    summary:hover,
.sort-control .sort-control-popup .sort-control-option:hover,
.packages .packages-item:hover,
.publishers .publishers-item:hover,
.search-form-container.experimental .search-form-linked-checkbox:hover,
.home-block .mini-list-item:hover {
      background: var(--pane01);
    }

    .pagination > li {
      background: var(--pane01);
    }

    .site-header,
header {
      background: var(--pane06);
    }
    .site-header .site-header-search .site-header-search-input,
header .site-header-search .site-header-search-input {
      background-color: var(--pane06);
    }

    .-wide-header-detail-page .detail-tabs-wide-header,
.-wide-header-detail-page .detail-header,
._banner-bg {
      background: var(--pane01);
    }

    header a,
header p,
header li {
      color: var(--textColor);
    }

    .tt-input {
      background-color: var(--pane01);
      color: var(--textColor);
    }

    .self-crumb {
      color: var(--gray);
    }

    .detail-tabs-header {
      background: var(--pane01);
    }
    .detail-tabs-header > .tab-button, .detail-tabs-header > .tab-link > a {
      color: var(--gray);
    }
    .detail-tabs-header > .tab-button:hover, .detail-tabs-header > .tab-link > a:hover {
      border-bottom-color: var(--gray);
    }
    .detail-tabs-header > .tab-button.-private, .detail-tabs-header > .tab-link.-private > a {
      color: var(--activeRed);
    }
    .detail-tabs-header > .tab-button.-private:hover, .detail-tabs-header > .tab-link.-private > a:hover {
      border-bottom-color: var(--activeRed);
    }

    .landing-main {
      background: transparent;
    }

    .home-block.home-block-ff, .home-block.home-block-pow {
      background: transparent;
    }
    .home-block .mini-list-item {
      background: var(--pane06);
    }
    .home-block .mini-list-item:hover .mini-list-item-body:before {
      background: transparent;
    }
    .home-block .mini-list-item .mini-list-item-body:before {
      background: transparent;
    }

    .search-bar > .input {
      background: var(--pane16);
    }

    .search-form-container.experimental .search-form-linked-checkbox a {
      color: var(--gray);
    }
    .search-form-container.experimental .search-form-linked-checkbox:hover {
      background: var(--pane01);
    }

    .detail-info-box > .title:not(:first-child) {
      border-top-color: var(--gray);
    }

    .markdown-body img,
.markdown-body table tr {
      background: transparent;
    }

    .markdown-body hr {
      background: var(--gray);
    }

    .markdown-body h1,
.markdown-body h2 {
      border-bottom-color: var(--gray);
    }

    pre {
      border-color: var(--pane06);
      background: transparent;
    }

    .source-code pre {
      padding: 16px;
    }

    .source-code pre,
.markdown-body .highlight pre,
.markdown-body pre,
.markdown-body code,
code {
      overflow: auto;
      font-size: 85%;
      line-height: 1.45;
      background-color: #132030;
      border-radius: 3px;
    }
    .source-code pre code,
.source-code pre tt,
.markdown-body .highlight pre code,
.markdown-body .highlight pre tt,
.markdown-body pre code,
.markdown-body pre tt,
.markdown-body code code,
.markdown-body code tt,
code code,
code tt {
      color: aliceblue;
      display: inline;
      max-width: auto;
      padding: 0;
      margin: 0;
      overflow: visible;
      line-height: inherit;
      word-wrap: normal;
      background-color: transparent;
      border: 0;
    }
    .source-code pre .hljs-comment,
.source-code pre .hljs-quote,
.markdown-body .highlight pre .hljs-comment,
.markdown-body .highlight pre .hljs-quote,
.markdown-body pre .hljs-comment,
.markdown-body pre .hljs-quote,
.markdown-body code .hljs-comment,
.markdown-body code .hljs-quote,
code .hljs-comment,
code .hljs-quote {
      color: #9a9a5c;
      font-style: italic;
    }
    .source-code pre .hljs-tag,
.source-code pre .hljs-name,
.source-code pre .hljs-attribute,
.markdown-body .highlight pre .hljs-tag,
.markdown-body .highlight pre .hljs-name,
.markdown-body .highlight pre .hljs-attribute,
.markdown-body pre .hljs-tag,
.markdown-body pre .hljs-name,
.markdown-body pre .hljs-attribute,
.markdown-body code .hljs-tag,
.markdown-body code .hljs-name,
.markdown-body code .hljs-attribute,
code .hljs-tag,
code .hljs-name,
code .hljs-attribute {
      color: #2dfd33;
      font-weight: normal;
    }
    .source-code pre .hljs-built_in,
.source-code pre .hljs-builtin-name,
.markdown-body .highlight pre .hljs-built_in,
.markdown-body .highlight pre .hljs-builtin-name,
.markdown-body pre .hljs-built_in,
.markdown-body pre .hljs-builtin-name,
.markdown-body code .hljs-built_in,
.markdown-body code .hljs-builtin-name,
code .hljs-built_in,
code .hljs-builtin-name {
      color: #dfe22e;
    }
    .source-code pre .hljs-string,
.source-code pre .hljs-doctag,
.markdown-body .highlight pre .hljs-string,
.markdown-body .highlight pre .hljs-doctag,
.markdown-body pre .hljs-string,
.markdown-body pre .hljs-doctag,
.markdown-body code .hljs-string,
.markdown-body code .hljs-doctag,
code .hljs-string,
code .hljs-doctag {
      color: #ff2f8f;
    }
    .source-code pre .hljs-keyword,
.source-code pre .hljs-selector-tag,
.source-code pre .hljs-subst,
.markdown-body .highlight pre .hljs-keyword,
.markdown-body .highlight pre .hljs-selector-tag,
.markdown-body .highlight pre .hljs-subst,
.markdown-body pre .hljs-keyword,
.markdown-body pre .hljs-selector-tag,
.markdown-body pre .hljs-subst,
.markdown-body code .hljs-keyword,
.markdown-body code .hljs-selector-tag,
.markdown-body code .hljs-subst,
code .hljs-keyword,
code .hljs-selector-tag,
code .hljs-subst {
      color: #69ece6;
      font-weight: bold;
    }
    .source-code pre .hljs-deletion,
.markdown-body .highlight pre .hljs-deletion,
.markdown-body pre .hljs-deletion,
.markdown-body code .hljs-deletion,
code .hljs-deletion {
      background: #652929;
    }
    .source-code pre .hljs-addition,
.markdown-body .highlight pre .hljs-addition,
.markdown-body pre .hljs-addition,
.markdown-body code .hljs-addition,
code .hljs-addition {
      background: #1d481d;
    }

    dd {
      color: var(--gray);
    }
  }
}
