/* ==================== Cascading Style Sheets ==================== */

@charset       "UTF-8";
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document 
url-prefix(http://reader.livedoor.com/reader/) {

/* ===================================================================
 *  My Feed
 *================================================================= */

/* Hidden
 *------------------------- */
/* #right_body */
#right_top_navi,
.GuideNavigationWrapper,
#GuideHeadBox,
#StaffBlogBox,
.FullBox,
.HintAndTipsWrapper,
.RecommendBox,
#RankingHeadBox,

/* 広告と検索バー */
#ads_top,
#ads_bottom,
#header,

/* Readerロゴ */
#reader_logo > A:last-child,

/* .r25_box */
.ads_add_reader,
#right_bottom > DIV:first-child + DIV > SPAN:first-child > A,
.ads_title,
#right_bottom > DIV:first-child + DIV > SPAN:first-child + SPAN > A,
.ads_url,
#right_bottom > DIV:first-child + DIV > SPAN:last-child > A,

/* Feed下部の新しい記事と過去記事へのリンク */
#feed_paging

{
display: none !important;
}

/* .r25_box 非表示時の調整 */
.r25_box {
padding-top: 0px !important;
padding-bottom: 0px !important;
height: 0px !important;
}


/* ================================================================ */

/* Customize
 *------------------------- */
/* #control表示階層の指定 */
#control {
position: fixed !important;
z-index: 200 !important;
}
/* #container表示の調整 */
#container {
position: fixed !important;
top: 30px !important;
}
#left_container {
position: fixed !important;
padding-top: 4px !important;
}
#subs_buttons,
#subs_toolbar {
padding-top: 0px !important;
padding-bottom: 0px !important;
}

/* フォルダを開いたときにスクロールバーでずれない様に */
#subs_container {
overflow-y: scroll !important;
}

/* タイトルを１行に */
#manage_item,
.treeitem {
white-space: nowrap !important;
height: auto !important;
}

/* ツリー背景色 */
#subs_container {
background-color: rgb(245, 245, 245) !important;
}

/* マイフィードをアイコンのみに */
#myfeed_tab .bottom {
display:none !important;
}

/* ヘッドラインをアイコンのみに */
#headlineMode > A > SPAN {
display:none !important;
}

/* マイフィードの表示を調節 */
#myfeed_tab {
position: fixed !important;
z-index: 200 !important;
width: 28px !important;
height: 22px !important;
margin-top: 4px !important;
}

/* ヘッドラインの表示と位置を調節 */
#headlineMode > A,
#headlineMode {
position: fixed !important;
z-index: 200 !important;
width: 27px !important;
height: 22px !important;
margin-top: 4px !important;
right: auto !important;
left: 33px !important;
}

/* コントロールパネルの表示を調節 */
.buttons li {
margin-right: 0px !important;
}

/* コントロールパネルを移動 */
#control_buttons {
position: fixed !important;
z-index: 200 !important;
right: auto !important;
left: 66px !important;
}

/* キャラクターを移動 */ 
#message_box {
position: fixed !important;
z-index: 210 !important;
right: auto !important;
top: -7px !important;
left: 470px !important;
}
/* 非表示の場合 
{
display: none !important;
} */

/* 未読数とエントリー数を移動 */
#total_unread_count {
position: fixed !important;
z-index: 210 !important;
right: auto !important;
top: 6px !important;
height: 20px !important;
left: 257px !important;
border-bottom: #a5c5ff solid 2px !important;
background-color: rgb(245, 245, 245) !important;
padding-top: 2px !important;
padding-left: 3px !important;
padding-right: 3px !important;
}
/* 非表示の場合 
{
display: none !important;
} */

/* フォントサイズ調整アイコンの表示を調節 */
.fontpad {
position: fixed !important;
z-index: 200 !important;
top: 5px !important;
right: 0px !important;
}
/* 非表示の場合 ※1 my_menuとlivedoorリンクの位置調節
{
display: none !important;
} */

/* #my_menuの不要なテキストを隠しアイコン化に対応させる為に全体を一度非表示 */
#my_menu {
visibility: collapse !important;
}
/* livedoorリンク画像変更の為に一度非表示 */
#reader_logo > A:first-child {
visibility: collapse !important;
}
/* すべて不要な時は下記と置き換える
{
display: none !important;
} */

/* livedoorリンクを画像変更 */
#reader_logo > A:first-child::after {
visibility: visible !important;
 content: url("data:image/png;base64,") !important;
}

/* ガイドを アイコン化 */
#my_menu > SPAN:first-child + A + A::after {
visibility: visible !important;
content: url("data:image/png;base64,") !important;
}

/* ログアウトをアイコン化 */
#my_menu > A:last-child::after {
visibility: visible !important;
content: url("data:image/png;base64,") !important;
}

/* #my_menuアイコン間の調節 */
#my_menu > A:last-child {
margin-right: 0px !important;
margin-left: -60px !important;
}
#my_menu > SPAN:first-child + A + A {
margin-right: 0px !important;
margin-left: -80px !important;
}

/* #my_menuの位置調節 */
#my_menu {
position: fixed !important;
z-index: 210 !important;
top: -1px !important;
right: 64px !important; /* ※1の場合 right: 0px !important; */
height: 20px !important;
}

/* livedoorリンクの位置調節 */
#reader_logo > A:first-child {
position: fixed !important;
z-index: 210 !important;
top: 4px !important;
right: 134px !important; /* ※1の場合 right: 68px !important; */
left: auto !important;
}

/* 移動後の表示調整 */
#menu {
padding-top: 0px !important;
padding-bottom: 0px !important;
height: 0px !important;
}
#reader_logo {
padding-top: 0px !important;
padding-bottom: 0px !important;
height: 0px !important;
}

/* フィードの整理（フォルダ、アイテムの編集）画面の表示調節 */
/* 不要な部分を非表示 */
#right_body > BR,
#right_body > TEXTAREA:first-child + TEXTAREA + TEXTAREA + TEXTAREA + TEXTAREA + H2 + DIV + DIV + BR + H2 + DIV {
display: none !important;
}

/* 表示の調節 */
#right_body > TEXTAREA:first-child + TEXTAREA + TEXTAREA + TEXTAREA + TEXTAREA + H2 {
padding-top: 0px !important;
padding-bottom: 0px !important;
height: 22px !important;
}

#manage_foldertools,
#right_body > TEXTAREA:first-child + TEXTAREA + TEXTAREA + TEXTAREA + TEXTAREA + H2 + DIV + DIV,
#right_body > TEXTAREA:first-child + TEXTAREA + TEXTAREA + TEXTAREA + TEXTAREA + H2 + DIV + DIV + BR + H2,
#manage_control {
margin-top: 0px !important;
margin-bottom: 0px !important;
}

#right_body > TEXTAREA:first-child + TEXTAREA + TEXTAREA + TEXTAREA + TEXTAREA + H2 + DIV + DIV + BR + H2 {
padding-top: 0px !important;
padding-bottom: 2px !important;
height: 0px !important;
}

/* #manage_offsetの移動とテキスト追加 */
#manage_offset {
position: absolute !important;
font-size: smaller !important;
top: 12px !important;
left: 220px !important;
right: auto !important;
}
#manage_offset::before {
visibility: visible !important;
content: "[" !important;
}
#manage_offset::after {
visibility: visible !important;
content: "]" !important;
}

/* ================================================================ */

/* Feed
 *------------------------- */
/* 画像の表示サイズ調整 */
div.body img {
max-width: 50% !important; /* 好みの大きさの％に変更して下さい */
height: auto !important;
}

/* "元記事"をURI変換 */
div.item_info {
text-indent: -3em !important;
}
div.item_info a {
visibility: hidden !important;
}
div.item_info a::after {
font-size: smaller !important;
visibility: visible !important;
content: attr(href) !important;
}

}


/* ===================================================================
 *  Headline Mode
 *================================================================= */

@-moz-document url-prefix("http://reader.livedoor.com/headline/") {

/* 不要部分を非表示 */
#functionHeader > P,
#functionHeader > FORM,
#contentHeader > H1 > A:last-child,
#tagline,
#contentHeader > UL,
#functionHeader > UL > LI:last-child,
#megaText,
#pager_footer {
display: none !important;
}

/* 上部コンテンツリンク(UL)をアイコン化の為に一度非表示  */
#functionHeader > UL {
visibility: collapse !important;
}
/* 画像変更の為に一度非表示 */
#contentHeader > H1 > A:first-child {
visibility: collapse !important;
}
/* すべて不要な時は下記と置き換える
{
display: none !important;
} */

/* コントロールボックスの表示調節 */
#contentsControlBox {
margin-top: 2px !important;
margin-bottom: -2px !important;
}

/* （上部）ページ移動リンクを移動 */
#pager > UL > LI:last-child {
position: absolute !important;
top: 1px !important;
left: auto !important;
right: 3px !important;
}
#pager > UL > LI:first-child {
position: absolute !important;
top: 1px !important;
left: auto !important;
right: 65px !important;
}
#pager > UL > LI:first-child + LI {
position: absolute !important;
width: auto !important;
top: 1px !important;
left: auto !important;
right: 129px !important;
}

/* 移動後の表示調節 */
#pager {
padding-top: 0px !important;
padding-bottom: 0px !important;
height: 1px !important;
}

/* livedoorリンク画像変更 */
#contentHeader > H1 > A:first-child::after {
visibility: visible !important;
 content: url("data:image/png;base64,") !important;
}

/* livedoorリンクの位置調節 */
#contentHeader > H1 > A:first-child {
position: absolute !important;
top: -3px !important;
right: auto !important;
left: 150px !important;
z-index: 200 !important;
}

/* ULの移動 */
#functionHeader > UL {
position: absolute !important;
top: 6px !important;
right: auto !important;
left: 312px !important;
z-index: 200 !important;
}

/* ULのアイコン化 */
#functionHeader > UL > LI:first-child > A::after {
visibility: visible !important;
content: url("data:image/png;base64,") !important;
}
#functionHeader > UL > LI:first-child + LI > A::after {
visibility: visible !important;
content: url("data:image/png;base64,") !important;
}
#functionHeader > UL > LI:first-child + LI + LI > A::after {
visibility: visible !important;
content: url("data:image/png;base64,") !important;
}
#functionHeader > UL > LI:first-child + LI + LI + LI > A::after {
visibility: visible !important;
content: url("data:image/png;base64,") !important;
}

/* アイコン間の調節 */
#functionHeader > UL > LI:first-child,
#functionHeader > UL > LI:first-child > A {
margin-right: 0px !important;
margin-left: 0px !important;
padding: 0px !important;
}
#functionHeader > UL > LI:first-child + LI,
#functionHeader > UL > LI:first-child + LI > A {
margin-right: 0px !important;
margin-left: -25px !important;
padding: 0px !important;
}
#functionHeader > UL > LI:first-child + LI + LI,
#functionHeader > UL > LI:first-child + LI + LI > A {
margin-right: 0px !important;
margin-left: -18px !important;
padding: 0px !important;
}
#functionHeader > UL > LI:first-child + LI + LI + LI,
#functionHeader > UL > LI:first-child + LI + LI + LI > A {
margin-right: 0px !important;
margin-left: -25px !important;
padding: 0px !important;
}

/* 移動後の表示調節 */
#commonHeader {
visibility: collapse !important;
} 
#functionHeader {
padding-top: 0px !important;
padding-bottom: 0px !important;
height: 0px !important;
margin-top: 0px !important;
margin-bottom: 0px !important;
} 
#contentHeader {
padding-top: 0px !important;
padding-bottom: 0px !important;
height: 0px !important;
}

/* 背景色 */
#main {
background-color: rgb(245, 245, 245) !important;
}

}

/* ============================ Finish ============================ */
