css:
@-moz-document domain("squared.cf") {
img[src*="logofull"]{ content: url("https://cdn.discordapp.com/attachments/523625108850343959/948701037076840518/squarednobe.png"); }


.navbar-dark.bg-dark {
   background: #7474bf;
    background: -webkit-gradient(linear,left top,right top,from(#348ac7),to(#7474bf));
    border-bottom: 0;
}

body {
    color: #7f7f7f!important;
}

.navbar-dark .navbar-nav .nav-link  {
    color: #d7edf7
}

.navbar-dark .navbar-brand, .navbar-dark .navbar-brand:focus, .navbar-dark .navbar-brand:hover {
    color: #d7edf7
}

.nav-scroller.navbar-dark.bg-dark {
    background: #545486;
    background: linear-gradient(90deg,#346ca1,#5562a2);
}

.nav-scroller .nav {
    display: flex;
    flex-wrap: nowrap;
    padding-bottom: 1rem;
    margin-top: -1px;
    width: 100%;
    overflow-x: auto;
    text-align: center;
    white-space: nowrap;
    -webkit-overflow-scrolling: touch;
}

.nav-underline .nav-link {
    padding-top: 0.75rem;
    padding-bottom: 0.75rem;
    color: #d7edf7!important;
    font-size: .875rem;
}

.btn-success {
    color: #fff;
    background-color: #01b35b;
    border-color: #01b35b;
}

body {
    background: #eceef2;
}

.card {
    position: relative;
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-orient: vertical;
    -webkit-box-direction: normal;
    -ms-flex-direction: column;
    flex-direction: column;
    min-width: 0;
    word-wrap: break-word;
    color: #636c72;
    background-color: #fff;
    background-clip: border-box;
    border: 1px solid rgba(0,0,0,.125);
    border-radius: 0.25rem;
}

.text-muted {
    color: #636c72!important;
}

.footer>.container {
    background: #fff;
    color: #d7edf7;
    border: 1px solid hsla(0,0%,96.5%,.125);
}

.btn-theme {
    color: #636c72;
    background-color: #fff;
    border-color: #35383c;
}

.input-group>.custom-select:not(:last-child), .input-group>.form-control:not(:last-child) {
    border-top-right-radius: 0;
    color: #636c72;
    background-color: #fff;
    border-color: #0090e4;
    border-bottom-right-radius: 0;
}

.card-header:first-child {
    border-radius: calc(0.25rem - 1px) calc(0.25rem - 1px) 0 0;
    background-color: #f7f7f7;
}

.text-light {
    color: #212529!important;
}

.bg-dark {
    background-color: #f7f7f7!important;
}

.text-white {
    color: #212529!important;
}

.badge-warning {
    color: #212529;
    background-color: #ffb200;
}

.badge-success {
    color: #fff;
    background-color: #01b35b;
}

.btn-outline-secondary {
    background-color: #6c747b!important;
    color: #fff;
    border-color: #f7f7f7;
}

.game-card {
    width: 150px;
    display: inline-block;
    background-color: #fff!important;
    background: #fff;
    color: #000000;
    border-radius: 3px;
    transition: box-shadow .2s linear;
}

.page-link {
    background-color: #eceef2;
    border: 1px solid #dee2e6;
}

.page-item.active .page-link {
    z-index: 1;
    color: #fff;
    background-color: #0090e4;
    border-color: #0090e4;
}

.table {
    color: #000000;
}

.bg-info {
    background-color: #c076c8!important;
    color: #fff!important;
}

.badge-info {
    color: #fff;
    background-color: #c076c8;
}

.forum-section-info {
    width: 100%;
    background-color: #fafafa;
    color: 000000!important;
}

.forum-post {
    width: 100%;
    padding: 1rem;
    background-color: #fbfbfb;
    display: block;
    transition: all .2s linear;
}

.breadcrumb {
    background: #f7f7f7;
}

.page-item.disabled .page-link {
    color: #868e96;
    background-color: #f7f7f7;
    border-color: #868e96;
}

.finobe-dark .forum-voter > .up[data-v-73be696c], .finobe-dark .forum-voter > .down[data-v-73be696c] {
    background-color: #f7f7f7;
    color: #868e96;
}

.form-control {
    background: #f7f7f7;
    color: #ececec;
    border: 1px solid hsla(0,0%,96.5%,.125);
}

pre {
    color: #636c72;
}

.btn-secondary {
    color: #868e96;
    background-color: #f7f7f7;
    border-color: #868e96;
}

.dropdown-menu {
    background-color: #f7f7f7;
}

.dropdown-item, .dropdown-item:focus {
    color: #000000!important;
    background: #f7f7f7;
}

.alert-warning {
    background: #ffb300;
    color: #fff;
}

.alert-success {
    background: #01b35b;
    color: #fff;
}
}
