css:
@-moz-document url("https://www.google.com.br/"), url("https://www.google.com/"), url-prefix("https://www.google.com.br/webhp"), url-prefix("https://www.google.com/webhp")
{
  #hplogo
  {
    display: none !important;
  }

  #lga
  {
    background-image: url("https://spinmodern.com/sm_uploads/2015/09/google-logo-white.png")!important;
    background-position: center 111px !important;
    background-repeat: no-repeat !important;
    background-size: 275px 95px !important;
  }

  body
  {
    background-image: url(https://i.pinimg.com/236x/09/54/6b/09546b2bb17de369c7a4fdd0bdda50ad.jpg)!important;
    background-attachment: fixed!important;
    background-repeat: no-repeat!important;
    background-size: cover!important;
  }

  .Mw2I7
  {
    border: 1px solid #000000 !important;
    background-color: #000 !important;
  }

  .RNNXgb,
.aajZCb,
.iKjWAf
  {
    background: #000 !important;
  }

  a.aciXEb
  {
    background: rgba(2, 2, 2, 0) !important;
  }

  .mitem.msel a,
cite,
.w,
#prs a:visited,
#prs a:active,
.q:active,
.q:visited,
.kl:active,
.tbotu,
.xXEKkb
  {
    color: rgb(159, 108, 206) !important;
  }

  .gb_pb
  {
    border-bottom-color: rgba(0, 0, 0, .925)!important;
  }

  .jhp input[type="submit"],
#hdtb-tls,
._gb_Ea,
gb_yb,
.gb_yb:hover
  {
    background-color: #000!important;
    background: #000!important;
    background-image: none!important;
    color: #fff!important;
    border: 0!important;
    transition: all .1s ease-out;
    cursor: pointer!important;
  }

  .sbct:hover
  {
    background: #333 !important;
  }

  #footcnt
  {
    visibility: hidden !important;
  }

  #SIvCob
  {
    color: #fff!important;
    font-weight: bold!important;
  }

  *
  {
    font-weight: bold!important;
  }

  ::-webkit-scrollbar
  {
    width: 9px;
  }

  ::-webkit-scrollbar-track
  {
    background: rgb(202, 153, 229);
  }

  ::-webkit-scrollbar-thumb
  {
    background: #e9ddf8;
  }

  ::-webkit-scrollbar-thumb:hover
  {
    background: rgb(0, 0, 0);
  }

  #prm
  {
    display: none !important;
  }
}
