css:
/* If you don't have your profile configured to always 
redirect you to old.reddit.com, you need to
replace the domain like this:
@-moz-document domain(old.reddit.com) {
*/
@-moz-document domain(reddit.com) {
.sitetable, .commentarea {
    width: min(200ch, calc(100% - 2ch));
    overflow: auto;
    margin-right: auto;
    margin-left: auto;
}

.commentarea {
    box-sizing: border-box;
}

.content .tagline {
    font-size: 1rem;
    margin-top: .25em;
    margin-bottom: .5em;
}


.content .tagline .subreddit {
    font-weight: bold;
}

.sitetable p, .commentarea p, .content p, .content header, .content .search-title, .content .title {
    font-size: 1.5rem;
}

.content .entry {
    margin-bottom: 1rem;
}

.content .usertext-body p {
    line-height: 1;
}

.link {
    margin: 0;
}

.bylink.comments {
    font-size: 1.25rem;
    font-weight: bold;
    color: navy !important;
}

.sr-list, .srdrop {
    font-size: 1rem !important;
    text-transform: none !important;
}

.srdrop.inuse.drop-choices {
    max-height: 80vh;
    display: flex;
    flex-wrap: wrap;
    gap: 0.25rem;
    width: min(80ch, 75vw);
    overflow: auto;
    overscroll-behavior: contain;
    padding: .25rem;
}

.srdrop.drop-choices .choice {
    width: 25ch;
    white-space: break-spaces;
    overflow-wrap: break-word;
    padding: 0;
    min-height: 1.5em;
    line-height: 1;
    display: inline-flex;
    align-items: center;
}
}
