css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(forum.signonsandiego.com), domain(forums.signonsandiego.com) {

body 
{ 
    background       : #ffffff !important;
    background-image : url('http://i50.tinypic.com/i1ffjo.jpg')  !important; 
}


#contentwrap
{
    width : 100% !important;
}

#container 
{
    width     : 100% !important;
    max-width : 1280px !important;
}

.tborder
{ 
    font-size     : 13pt  !important;
    margin-bottom : 15px !important;
}

table[id^="post"] tr.even,
div[id="post_message_"] tr.even td.alt2,
#posts tr.even td.alt2
{
    font-size  : 13pt !important;
    background : #003766   !important;
    color      : #ffffff   !important;
}

#posts table[id^="post"] div.smallfont
{
    color : #ffffff !important;
}

table[id^="post"] div.smallfont strong
{
    color : #000088 !important;
}


table[id^="post"] td.thead
{
    background       : #fff9c4  !important;
    color            : #000000  !important;
    background-image : url('http://i48.tinypic.com/n1fo01.jpg') !important;
    border-bottom    : 1px solid #cccccc;
    border-top       : 1px solid #777777;
}


#chanleader,
#chanheader,
#nav,
#logo,
#utbar,
#toolbar,
#footer
{
   display : none !important;
}


div[id^="post_message"] 
{
   padding-top    : 5px !important;
   padding-bottom : 5px !important;
   line-height    : 25px !important;
}

div[id^="post_message"] a
{
   color           : #0000ff  !important;
   text-decoration : underline !important;
}


td[id^="td_threadtitle"] 
{
   padding-top    : 5px !important;
   padding-bottom : 5px !important;
}

table.tborder
{
    border : 1px solid #000088 !important;
}

#threadslist td
{
   background : #ffffff   !important;
}

#threadslist tr.even td
{
   background : #87c2f3   !important;
}


#threadslist .thead
{
    color      : #000000  !important;
}

#threadslist .thead a
{
   background-color : #87c2f3   !important;
}

.thead a
{
   color            : #0000ff !important;
   font-weight      : bold;
}

a.bigusername
{
   color       : #ffbc07 !important;
}

td[id^="td_post"]
{
    background : #ffffff !important;
    color      : #000000 !important;
}

#threadslist .smallfont
{
    color : #444444   !important;
}

#posts div[id^="post_message"] td.alt2 a,
div [id="post_message_"] td.alt2 a
{
    color      : #ffcc55  !important;
}

a:visited, 
a:hover
{
    text-decoration : none  !important;
}

#threadslist a
{
    color           : #000055 !important;
    font-weight     : normal  !important;
    text-decoration : none !important;
}

#threadslist a[id^="thread_title"][style^="font"]
{
    font-weight : bold !important;
}


#inlinemodform table[id^="post"] .alt2
{
    background : #003766   !important;
}

#inlinemodform table[id^="post"] .alt2 em
{
    color      : #ffffff   !important;
}

#inlinemodform table[id^="post"]  .alt2 a
{
   color       : #ffbc07 !important;
}

a,
a:visited,
a:hover
{
   text-decoration : none  !important;
}


}
