css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("deviantart.com") {
#logindock{position:fixed!important; -moz-border-radius:5px!important; height:26px!important; top:-3px!important; margin:0 18px!important;width:98%!important;}
#logindock div.ch-ctrl {-moz-border-radius:5px!important;height:26px!important;background: #27362f url(http://trezoid.webs.com/dAfiles/topbarback.gif)top left repeat-x !important; border-bottom-width:0!important;top:-3px!important;width:100%!important;color:#000!important;}
.bl, .br{display:none!important;}
.friendsmenu, .collectmenu{display:none!important;}

body.chatroom #top i.tr, body.chatroom #top i.tl {display:none!important;}

#lubber{display:none!important;}
.collections, #lub{width:95%!important;left:25px!important;-moz-border-radius:5px!important;}

#logindock .text span:first-child { color:transparent!important; }
#logindock .text{left:-40px!important;position:absolute!important;width:90%!important;}
#logindock span.logout a{background-image:none!important;}

#searchForm{position:fixed!important; top:-41px!important; right:10px!important;height:20px!important;z-index:201!important;background:none!important;}
#searchForm > a{display:none!important;}

#searchInput{background:none!important;}
.form{background:#C1D1BC none!important;height:18px!important; left:-3px!important;-moz-border-radius:3px!important;opacity:0.8!important;top:1px!important;}
#searchInput{top:-2px!important;left:1px!important;}
.select, .selected, #searchForm ul.select li a {height:18px!important;max-height:18px!important;top:0px!important;right:35px!important;}

div.popup2-search{position:fixed!important;}
.ibutton{-moz-border-radius-topright:5px!important;-moz-border-radius-bottomright:5px!important;height:18px!important;right:-1px!important;}

#top {height:65px!important;}
#moremenu-h{margin-top:30px!important;}
#moremenu{left:190px!important;}
h1{top:20px!important;}
#sitemap-more{width:1000px!important;}
div.popup2-moremenu{width:1000px!important;}

div.pager-messages div.page2 a.f{left:0px!important;}

.text a.blend{font-size:4px!important;color:transparent!important;visibility:hidden!important;}


}
