css:
/*
 * Repubblica.it - Dark Gray Theme Italian
 *
 * Autore      : Ossomasticato
 * Updated     : 09 Dicembre 2009
 * Descrizione : Modifica i colori e qualche altra cosetta su www.repubblica.it
 * Utilizzo    : Usare con l'estensione Stylish per Firefox (http://userstyles.org/) o copiarla nel file userContent.css di Firefox
 */






@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.repubblica.it") {


/* defaults for all links */

body { color:#000000 !important; 
       background-color:#262626 !important; }

#ccenter, #sottospalla, #aperturahp, #container, #riaperturahp, #riapertura2hp, #strillorot1, .newaddons, .gLettori, #blogLettori, .edizioni-locali, .generalbox, .service, #annunci, #topheader, #header, #cerca .newgoogle, #aperturalarge {
background-color:#262626 !important; }

#tabs, #ventiquattrore, #newprefooter { background:#262626 !important; }
#propone .blocs a, .deejay, #newfooter { background:#262626 !important; }

#bgspot { color:#FFFFFF !important; 
          background-color:#262626 !important; 
          padding-bottom: 15px!important; }

#topmenuv2 a:hover, #bgspot .menuv2 li a.currentmenu, 
#bgspot .menuv2 li a.currentmenu:visited, .menuv2 { color:#FFFFFF !important; 
                                                    background-color:#262626 !important; }

.menuv2 a { color:#FFFFFF !important; 
            background-color:#262626 !important; }

.txt12 a span { color:#FF0000; }
#ventiquattrore a:hover { background-color:#656565 !important; }



/* Colore dei link text della pagina */

#ventiquattrore a strong, #aperturahp a b span { color:#FF0000 !important; }

h1 a b span { color:#FF0000 !important; 
              background-color:#262626!important; }

.txt16 b:hover { color:#62EE62 !important; }

#aperturahp, #riapertura2hp { border-top-color:#298FC2 !important; 
                              border-top-style:solid !important; 
                              border-top-width:1px !important; 
                              border-bottom-color:#298FC2 !important; 
                              border-bottom-style:solid !important; 
                              border-bottom-width:1px !important; 
                              border-left-color:#298FC2 !important; 
                              border-left-style:solid !important; 
                              border-left-width:1px !important; 
                              border-right-color:#298FC2 !important; 
                              border-right-style:solid !important; 
                              border-right-width:1px !important; }

#aperturahp, #riaperturahp, #riapertura2hp { margin:0 7px 0 0 !important; }




/* Modifica il DIV Strillorot */


#strillorot1, .txt16 { color:#FFFFFF !important; }
#strillorot1 a { color:#FF0000 !important; }
#strillorot1 span a:hover { color:#6666FF !important; }
#strillorot1 span a b span, #strillorot1 span b span, #strillorot1 a b span u, #strillorot1 b span { color:#FF0000 !important; }

#strillorot1 { border-top-color:#298FC2 !important; 
               border-top-style:solid !important; 
               border-top-width:1px !important; }

#strillorot1 .txt16 a b u { color:#FFFFFF !important; }
#strillorot1 .txt16 a b u:hover { color:#62EE62 !important; }
#strillorot1 .txt16 a b { color:#FFFFFF !important; }
#strillorot1 .txt16 a b:hover { color:#62EE62 !important; }  

#strillorot3, #strillorot2 { background:#262626 !important; }
#strillorot3 span a:hover, #strillorot2 span a:hover { color:#6666FF !important; }
#strillorot2, .txt16 { color:#FFFFFF !important; }
#strillorot3, .txt16 { color:#FFFFFF !important; }
#strillorot2 span a u { color:#62EE62 !important; }

#strillorot2 { border-top-color:#298FC2 !important; 
               border-top-style:solid !important; 
               border-top-width:1px !important; }

#strillorot3 { border-top-color:#298FC2 !important; 
               border-top-style:solid !important; 
               border-top-width:1px !important; }

#strillorot2 span a b span { color:#FF0000 !important; }
#strillorot2 span a b span:hover { color:#6666FF !important; }
#strillorot3 span a b span { color:#FF0000 !important; }
#strillorot3 span a b span:hover { color:#6666FF !important; }
#strillorot1 span a b span { color:#FF0000 !important; }
#strillorot1 span a b span:hover { color:#6666FF !important; }



/* Modifica il DIV Sottospalla */

.strillosottospalla .txt12 a b span { color:#FFFFFF !important; }
.strillosottospalla .txt12 a b span:hover { color:#999999 !important; }
.strillosottospalla a { color:#FFFFFF !important; }
.strillosottospalla a:hover { color:#999999 !important; }
.strillosottospalla a b span { color:#FFFFFF !important; }
.strillosottospalla a b span:hover { color:#999999 !important; }
.strillosottospalla .txt12 b { color:#FFFFFF !important; }
.strillosottospalla .txt12 b:hover { color:#999999 !important; }

#centrohp .strillo { border-left:1px solid #CCCCCC !important; 
                     border-right:1px solid #CCCCCC !important; 
                     padding:11px 4px 11px 4px !important; }



/* Modifica il colore della firma */

.firmahm { color:#FBE464 !important; }



/* Modifica i colori dei link e del testo in generale */

a:link { color:#FFFFFF !important; 
         background:#262626 !important; }

a:hover { color:#62EE62 !important; }
.txt12 { color:#FFFFFF !important; }



/* Imposta il colore dell'aggiornamento sotto il brand */

#update { color:#62EE62 !important; }





/* Modifica il DIV Aperturahp */

#aperturahp .txt12 a u { color:#62EE62 !important; }
#aperturahp .txt12 a u:hover { color:#6666FF !important; }
#aperturahp h1.txt25 a b { color:#FFFFFF !important; }
#aperturahp h1.txt25 a b:hover { color:#62EE62 !important; 
                                 background:#262626 !important; }

#aperturahp a b span:hover { color:#6666FF !important; }
#aperturahp a b span:visited { color:#999999 !important; }

#aperturahp { border-top-color:#298FC2 !important; 
              border-top-style:solid !important; 
              border-top-width:1px !important; 
              border-bottom-color:#298FC2 !important; 
              border-bottom-style:solid !important; 
              border-bottom-width:1px !important; 
              border-left-color:#298FC2 !important; 
              border-left-style:solid !important; 
              border-left-width:1px !important; 
              border-right-color:#298FC2 !important; 
              border-right-style:solid !important; 
              border-right-width:1px !important; }




/* Modifica il DIV Riaperturahp */

#riaperturahp .txt12 a b span { color:#FF0000 !important; }
#riaperturahp .txt12 a b span:hover { color:#6666FF !important; }
#riaperturahp .txt12 a u { color:#62EE62 !important; }

#riaperturahp .txt21 a b { color:#FFFFFF !important; 
                           background:#262626 !important; }

#riaperturahp .txt21 a b:hover { color:#62EE62 !important; }
#riaperturahp a b span { color:#FF0000 !important; }
#riaperturahp a b span:hover { color:#6666FF !important; }
#riaperturahp .txt12 a u:hover { color:#6666FF !important; }

#riaperturahp { border-left-color:#298FC2 !important; 
                border-left-style:solid !important; 
                border-left-width:1px !important; 
                border-right-color:#298FC2 !important; 
                border-right-style:solid !important; 
                border-right-width:1px !important; }



/* Modifica il DIV Dirette-dashboard */

#dirette-dashboard .dirette-container li a strong { color:#FF0000 !important; }
#dirette-dashboard h4 a { color:#FF0000 !important; 
                          background:#262626 !important; }

#dirette-dashboard h4 a:hover { color:#6666FF !important; }



/* Modifica il DIV Riapertura2hp */

#riapertura2hp .txt12 a b span { color:#FF0000 !important; }
#riapertura2hp .txt12 a b span:hover { color:#6666FF !important; }
#riapertura2hp .txt18 a b { color:#FFFFFF !important; }
#riapertura2hp .txt12 a u { color:#62EE62 !important; }
#riapertura2hp .txt12 a u:hover { color:#6666FF !important; }

#riapertura2hp .txt18 a b:hover { color:#62EE62 !important; 
                                  background-color:#262626!important; }

#riapertura2hp .txt12 b span { color:#FF0000 !important; }



/* Modifica il DIV colonnanotizie */

#colonnanotizie .strillo .txt12 a u { color:#62EE62 !important; }
#colonnanotizie .strillo .txt12 i { color:#62EE62 !important; }
#colonnanotizie .strillo .txt12 i { color:#62EE62 !important; }

#colonnanotizie .strillo .txt16 a b { color:#FFFFFF !important; 
                                      background-color:#262626!important; }

#colonnanotizie .strillo .txt16 a b:hover { color:#62EE62 !important; }
#colonnanotizie .strillo .txt12 a b span { color:#FF0000 !important; }
#colonnanotizie .strillo .txt12 a b span:hover { color:#6666FF !important; }
#colonnanotizie .strillo .txt12 a b { color:#62EE62 !important; }
#colonnanotizie .strillo .txt12 a u:hover { color:#6666FF !important; }
#colonnanotizie .strillo .txt12 b span { color:#FF0000 !important; }
#colonnanotizie .strillo h1.txt12 a b:hover { color:#6666FF !important; }



/* Modifica il DIV BlogLettori */

.gLettori ul li a strong { color:#3399FF !important; }
.gLettori cite { color:#FF6600 !important; }



/* Modifica il DIV BlogLettori */

.service h6 { color:#FF6600 !important; }
.service h5 { color:#FF6600 !important; }



/* Modifica il DIV Opinioni */


#opinioni-v2 h5 { color:#6666FF !important; }

#opinioni-v2 { border:3px ridge !important; 
               border-color:#FF0000 !important; }



/* Modifica il DIV Propone */

#propone .blocs h3 { color:#FF6600 !important; }
#propone .blocs h3 a { color:#FF6600 !important; }



/* sposta il brand al centro del DIV */

#brand { float:none; 
         margin:15px 0 0 20px; 
         width:300px; 
         padding-left:160px !important; }



/* Modifiche per gli articoli */

.articolo h1, .categoria, #testo, .copyright, .date { color:#FFFFFF !important; }
.articolo h2 { color:#FFFFFF !important; }

.apertura-bg > h1, .apertura-bg > h1 a,
.apertura-bg > h1 a:visited { color:#FFFFFF !important; 
                              font-size:24px !important; 
                              font-weight:normal !important; 
                              line-height:24px; 
                              margin:0 0 5px !important; 
                              background:transparent !important; }

.apertura p a, .apertura p a:visited { background:transparent !important; }
.articles p, .articles p a b span { color:#FFFFFF !important; }
.articles p a b span:hover { color:#6666FF !important; }

#kw-strilli { background-color:#262626 !important; 
              border:2px ridge !important; }

.box h5 { color:#FFFFFF !important; }
.apertura-bg { background-color:#002E46 !important; }
.apertura-bg h2 a b, .apertura-bg h2 a { background-color:#002E46 !important; }
h4, h5, h6 { color:#FFFFFF !important; }
.clearfix > li > dl > dt > a { color:#FFFFFF !important; }
.clearfix > li > dl > dt > a:hover { color:#62EE62 !important; }
.txt12 > i { color:#FBE464 !important; }

#novita { background-color:#262626 !important;  
          border:2px ridge !important; }

#boxlistini #tabs ul#tabsNav li a.activeTab { background-color:#262626 !important; 
                                              color:#FFFFFF !important; }

tab1.activeTab a span:hover { color:#FFFFFF !important; }
.articolo h3 { color:#FF0000 !important; }
#testo a u { color:#62EE62 !important; }
#testo a u:hover { color:#6666FF !important; }
#multimedia .fotosxb p { color:#FBE464 !important; }
#cright .box-ultimo-minuto p { color:#FFFFFF !important; }
#multimedia h5 { color:#FF6600 !important; }



/* Piccole modifiche */

#sitilocali h5 { color:#6666FF !important; }
#newfooter p { color:#6666FF !important; }
.deejay-club p { color:#FFFFFF !important; }
.deejay-club { height:150px !important; }
#strilli-dj { padding-left:10px !important; }
.currentmenuhome { border-color:#6666FF !important; }

a.currentmenu:visited { background:#262626 !important; 
                        border-color:#6666FF !important; 
                        border-left:1px solid #6666FF !important;
                        border-right:1px solid #6666FF !important; }

#topmenuv2 a { background-color:#262626 !important; 
               border-left:1px solid #FF0000 !important; 
               border-right:1px solid #FF0000 !important; 
               border-top:1px solid #FF0000 !important; }

#topmenuv2 a:visited { color:#FF6600 !important; }
.menuv2 { border:1px solid #FF0000 !important; }
#bgspot .menuv2 a { border-right:1px solid #FF0000 !important; }
#topmenuv2 { background-color:#262626 !important; }
#tabs a:hover span { color:#262626 !important; }
#tabs a span { background-color:#262626 !important; }
.newaddons #mappe, .newaddons h5.cmeteo { background-color:#262626 !important; }
.newaddons h5 a { color:#FF6600 !important; }
#sbcolmaindet .categoria dl dd { background-color:#FFFFFF !important; }
#mappe { color:#FFFFFF !important; }
.deejay-club h5 { color:#FF6600 !important; }



/* Elimina le varie pubblicitÃ  */

.manchette-left, .manchette-right, #ge-network, #advstrip, .shared-box-speciale, #OAS-titolo-vetrina, .rep-shopping, #adv160x75lup, #adv160x75ldown, #adv160x75rdown, #adv300x250, #adv160x75rup, #adv160x600r, #adv180x150r, #annunci, #rrtg, .bluebox, #newborsa, #neworoscopo, #sudoku, #cright a img, iframe, .generalbox, #advtop, #adv180x150m, #adv300x250r, .super-vetrina, #videoreptv, #FinContentx101, #adv_Skin, #ads2 { display:none !important; }

}



@-moz-document domain("temi.repubblica.it") {

body { color:#000000 !important; 
       background-color:#262626 !important; }

#bgspot { color:#000000 !important; 
          background-color:#262626 !important; }

#header, #topmenuv2 { background-color:#262626 !important; }
h3.firmatariappello { color:#FFFFFF !important; }
.generalbox.paginefirme { background-color:#FFFFFF !important; }
#newprefooter p a, #newfooter p, #newfooter p a { color:#FFFFFF !important; }
#newprefooter p a:hover { color:#6666FF !important; }
#swp-sidebar-right { color:#FFFFFF !important; }




/* Elimina le varie pubblicitÃ  */
#topheader, #advstrip { display:none !important; }




}
