css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.recordchina.co.jp/") {
#rightpane,
#header,
#leftpane,
#centerpane,
#header_ad,
#newspage_title_entertainment,
#AdingoAd468x60,
#news_mobile_box,
iframe,
#info_link,
div[id*="bottom_ad"],
#footer,
#news_detail_photo_description
{
 display:none !important;
}

body
{
 background: transparent url(http://www.recordchina.co.jp/images/header_logo.jpg) no-repeat fixed right bottom !important;
}

#centerbox,
#contents_field,
#content,
#news_detail_box
{
 width:100% !important;
 background-color: transparent !important;
}

#groundbox
{
 width:99% !important;
 background-color: transparent !important;
}

#contents_field
{
 margin-top:0px !important;
}

#groundbox
{
 margin-top:0px !important;
}

#news_detail_title
{
 border-top:3px double #ccc !important;
 margin-top:1ex !important;
}

#news_detail_title
{
 color:#f00 !important;
}

table.news_detail_photos_chip,
#news_detail_photo
{
 border:1px solid #ccc !important;
}


}
