css:
@-moz-document domain(peazip.com){
   .app.flexrow > div:nth-of-type(2) {
      display: none;
   }
   .app.flexrow > div:nth-of-type(1) > div:nth-of-type(3) {
      width: 100vw !important;
   }
   .panelhead {
      max-width: 100vw !important;
      width: auto;
   }
   .mainblock.panelblock {
      flex-grow: 1;
   }
   .body {
      width: auto !important;
   }
   .pbody > canvas {
      width: 100% !important;
   }
}
