css:
@-moz-document regexp("https://www.acg-123.org/(index-htm-m-bbs.html|read-htm-tid-\\d+\\.html|hack.php|thread-htm-fid-\\d+\\.html|post.php|thread.php|index.php|).*") {
/*1. 自定义背景图*/
 body[onkeydown="try{keyCodes(event);}catch(e){}"] {
    background: url('/*[[background-image]]*/');
    background-attachment: fixed;
    background-repeat: no-repeat;
    background-size:cover;
    background-position: 0 0;
}
/*2. 布局调整*/

/*2.1. 工具栏*/
 #searchA {
    margin-top: 0 !important;
    overflow: visible;
}
.searchA.cc {
    padding-left: 5px;
}
.searchA_right.fr {
    display: none;
}
/*2.2. 帖子列表*/
 .tabA>.cc>.current>a {
    margin-top: 1px;
}
/*2.3. 帖子页面*/
 .readLevel.mb10+div.mb10 {
    display: grid;
    display: -webkit-grid;
    grid-template-columns: repeat(5, 1fr);
    column-gap: 4px;
    row-gap: 5px;
    margin: 0 8.5px 10px;
}
.readLevel.mb10>img {
    margin: auto;
}
.readLevel.mb10+div.mb10>br {
    display: none;
}
.tpc_content img {
    max-width: 931px;
    height: 100%;
}
/*2.4. 回帖页面*/
 .wy_left img {
    max-width: 110px;
    height: 100%;
}
.menu_select .hover {
    margin-top: 0px;
}
/*3. 圆角化*/

/*3.0. 顶栏和底栏*/
 .startbar-ui {
    border-radius: 5px 5px 0 0!important;
}
/*3.1. 工具栏*/
 #searchA {
    border-radius: 0 0 5px 5px;
}
#navA {
    border-radius: 5px 5px 0 0;
}
#td_mymenu {
    border-radius: 5px;
    border: 1px solid white;
}
.searchA.cc>form>.ip {
    border-radius: 5px 0 0 5px;
}
.searchA.cc>form>.ip+.s_select {
    border-radius: 0 5px 5px 0;
}
.ip+.s_select+button {
    border-radius: 5px;
    border: 1px solid white;
}
.searchA.cc>form>.ip+.s_select>ul {
    border-radius: 0 0 5px 5px;
}
/*3.2. 主体*/
 #pw_content {
    border-radius: 5px;
    border: unset;
}
.newInfor {
    border-radius: 5px 5px 0 0;
}
th>.hB {
    border-radius: 5px 0 0 0;
}
td>.hB {
    border-radius: 0 5px 0 0;
}
.tabB>.view-hover>.switchNavItem.current>a {
    border-radius: 5px 5px 0 0;
}
/* 3.3. Popup */
 .menuList, .pw_menu {
    border-radius: 5px;
}
/*3.4. 面包屑*/
 #breadCrumb {
    border-radius: 5px;
}
/*3.5. 帖子列表*/
 .content_ie>.hB {
    border-radius: 5px 5px 0 0;
}
.fastpost>.t {
    border-radius: 0 0 5px 5px;
}
.tabA>.cc>.current>a {
    border-radius: 5px 5px 0 0;
    border-top: 1px solid #aaaaaa;
}
.t>form>.floot {
    border-radius: 0 0 5px 5px;
}
/*3.6. 分页*/
 .pages>a, .pages>b {
    border-radius: 5px;
}
.pages>.pagesone {
    border-radius: 5px;
}
.pages>.pagesone>button {
    border-radius: 0 5px 5px 0;
}
/*3.7. 发帖与回复按钮*/
 span.btn, span.bt, span.btn2, span.bt2, span.bts, .post, .replay {
    border-radius: 5px;
    border: 1px solid white;
}
/*3.8. 帖子页面*/
 .readTop {
    border-radius: 5px 5px 0 0;
}
.t {
    border-radius: 5px;
}
.t>form>.b.h {
    border-radius: 5px 5px 0 0;
}
.face_img img {
    border-radius: 5px;
}
.fp_show_tab li a {
    border-radius: 5px 5px 0 0;
}
.fp_show_cont {
    border-radius: 5px;
}
/*3.9. 回帖页面*/
 #pw_content>form>.t>table>tbody>tr>td:first-child {
    border-radius: 5px 0 0 0;
}
#pw_content>form>.t>table>tbody>tr>td:last-child {
    border-radius: 0 5px 0 0;
}
.menu_select .hover {
    border-radius: 0 0 5px 5px;
}
.menu_select div {
    border-radius: 5px;
}
.w_title_ip {
    border-radius: 5px;
}
#icontab div {
    border-radius: 0 0 5px 5px;
}
.wind_editor_con {
    border-radius: 5px;
}
.w_editor_but {
    border-radius: 5px 5px 0 0;
}
.wy_top_but>i {
    border-radius: 5px;
}
.wy_top_but>i.current {
    border-radius: 5px 5px 0 0;
}
.w_options {
    border-radius: 0 0 5px 5px;
}
.w_editor_up_con {
    border-radius: 0 0 5px 5px;
}
.w_editor_up_con select {
    border-radius: 5px;
}
.w_editor_up .w_tab_a .current a {
    border-radius: 5px 5px 0 0;
}
.wy_tips>i {
    border-radius: 5px;
}
/*3.10. 输入控件*/
 .fp_content input {
    border-radius: 5px;
}
.fp_editor {
    border-radius: 5px;
}
.fp_editor_but {
    border-radius: 5px 5px 0 0;
}
.wind_editor input {
    border-radius: 5px;
}
/*4. 半透明化*/

/*4.0. 顶栏和底栏*/
 #top {
    background: rgb(255, 255, 255, 0.5)!important;
}
.startbar-ui {
    background: linear-gradient(rgb(234, 245, 250, 0.7), rgb(241, 249, 251, 0.7))!important;
    backdrop-filter: blur(2px)!important;
}
/*4.1. 工具栏*/
 .navA, .navAL, .navAR, .navA li, .navA li a, #td_mymenu {
    background: none;
}
#searchA {
    background: rgb(255, 255, 255, 0.7);
    backdrop-filter: blur(2px);
}
.searchA_right {
    background: none;
}
#navA {
    background-color: rgb(238, 128, 179, 0.7);
    backdrop-filter: blur(2px);
}
.navA>.cc>.current {
    background-color: rgb(241, 137, 199, 0.7);
}
.ip+.s_select+button {
    background: rgb(250, 239, 199, 0.7);
}
/*4.2. 主体*/
 #pw_content {
    background: rgb(255, 255, 255, 0.8);
}
.hB {
    background: linear-gradient(rgb(255, 255, 255, 0.8), rgb(243, 243, 243, 0.8));
}
.h {
    background: rgb(254, 237, 240, 0.5);
    border: none;
}
/*4.3. 面包屑*/
 #breadCrumb {
    background: rgb(255, 237, 239, 0.8);
}
/*4.4. 帖子列表*/
 .f_tree.cc {
    background: none;
}
.f_tree.cc>.content_thread.cc {
    background: none;
}
.tr4 td {
    background: rgb(249, 249, 249, 0.8);
}
.fastpost>.t {
    background: transparent;
}
.sidePd10>.tabA {
    background: rgb(244, 244, 244, 0.8);
}
/*4.5. 发帖与回复按钮*/
 .replay {
    background: rgb(255, 231, 193, 0.8);
    backdrop-filter: blur(2px);
}
span.btn span, span.bt span, span.btn2 span, span.bt2 span, span.bts span {
    background: none;
}
span.btn, span.bt, span.btn2, span.bt2, span.bts, .post {
    background: rgb(240, 139, 5, 0.8);
    backdrop-filter: blur(2px);
}
/*4.6. 帖子页面*/
 .floot_right {
    background: none;
}
.readTop {
    background: rgb(255, 237, 239, 0.5);
}
.t {
    background: rgb(255, 255, 255, 0.8);
}
.t>form>.floot {
    background: rgb(255, 255, 255, 0.8);
}
/*4.7. 回帖页面*/
.w_bt {
    background: none!important;
}
.w_btn {
    background: rgb(240, 139, 5, 0.8)!important;
    backdrop-filter: blur(2px)!important;
}
/*5. 图标替换*/
 #td_mymenu:after {
    content:'';
    display: inline-block;
    width: 7px;
    height: 4px;
    margin-left: 5px;
    margin-bottom: 1px;
    background: url(https://www.acg-123.org/images/wind8gray/down.png);
}
/*6. 事件*/
 .searchA.cc>form>.ip+.s_select:hover {
    border-radius: 0 5px 0 0;
}
}
