@-moz-document url-prefix("https://www.roblox.com"), url-prefix("https://web.roblox.com") {


@keyframes header-rainbow {
       0% {
              border-color: red;
       }
       14% {
              border-color: orange;
       }
       28% {
              border-color: yellow;
       }
       42% {
              border-color: #72fba2;
       }
       56% {
              border-color: #72dcfb;
       }
       80% {
              border-color: #2a3bce;
       }
       94% {
              border-color: #692ace;
       }
       100% {
              border-color: red;
       }
}
.rbx-header {
       animation: header-rainbow 13s infinite;
}

@keyframes rainbow


  {
    0% {background-color: red;
    border-color: red;
  }

  14%
  {
    background-color: orange;
    border-color: orange;
  }

  28%
  {
    background-color: yellow;
    border-color: yellow;
  }

  42%
  {
    background-color: green;
    border-color: green;
  }

  56%
  {
    background-color: blue;
    border-color: blue;
  }

  80%
  {
    background-color: purple;
    border-color: purple;
  }
                        94%
  {
    background-color: purple;
    border-color: purple;
  }
                        100%
  {
    background-color: purple;
    border-color: purple;
  }
                    }

