@-moz-document domain(outlook.office.com) {

/* Change the reading pane of email */
div.Vzh05neKQu87GUy5UviOc * {
    font-family: Ubuntu !important;
    font-weight: 300;
}
  
/* Change the reply pane of email */
/* It overwrites custom fonts in the email though */
