css:
@-moz-document domain("bebmetrostroi.7m.pl") {
body {
/*     background: radial-gradient(#2a2a3c, #2f2f44); */
}

.home-hd {
	background: none;
}

.content {
	background: none;
}

div#pct {
    filter: blur(0.35rem);
}

.menubar {
    background: linear-gradient(to right, #a34f4f, #8535c7);
/*     filter: blur(12px); */
}

.dropdown-item {
    padding: 0.35em 1.5rem;
    border-radius: 0.1cm;
}

.dropdown-menu {
    border-radius: 0.35cm;
}

.dropdown-item:hover, .dropdown-item:active, .dropdown-item:focus {
    color: #e0e0e0;
    background: linear-gradient(90deg, #a34f4f, #58287e);
	border-radius: 0.25rem;
	border-color: none;
}

.card {
    color: #e0e0e0;
    background: linear-gradient(to right, #a34f4f, #693891);
}

.card-header {
    background: none;
}

.invalid-feedback {
    color: #ffbdbd;
}

.btn-primary:hover, .btn-primary:active, .btn-primary:focus {
	background: linear-gradient(90deg, #a34f4f, #58287e);
}

.form-control:focus {
/*     color: #495057; */
/*     background-color: #fff; */
    border-color: #99467a;
/*     outline: 0; */
}
}
