css:
@-moz-document domain('kwaleeverse.com') {
    .logo img {
        filter: invert(1);
    }

	#masterBody {
        background-color: #2e2e2e;
    }
    
    #masterBody section {
        background-color: transparent !important;
    }

    #main-content>[class*="container"] {
        background-color: transparent !important;
    }
    
    #masterBody p, h1, h2, h3, h4, h5, h6, a, time, i, legend, div:not(#twitter-widget-0) {
        color: white !important;
    }

    #masterBody .gridster .Widget_Content_CircleView .widgetBody .carousel .circle-view li .details a, .gridster .Widget_TopicsContent_CircleView .widgetBody .carousel .circle-view li .details a {
        color: white !important;
    }
    
    #status-banner, #nav-bar-container {
        background-color: #2e2e2e;
        color: white;
    }

    .tl-eventcard {
        background-color: #4b4b4b !important;
        color: white !important;
    }

    .tl-eventcard a {
        color: rgb(218, 226, 98) !important;
    }

    .gridster .Widget_Rewards_give .widget-rewards-container {
        background-color: #4b4b4b !important;
        color: white !important;
    }

    .gridster .Widget_Rewards_give .widget-rewards-container .rewards-amount {
        background-color: #949494 !important;
    }

    #site-navigation>li, #site-navigation-more>li, #header-container.is-condensed #nav-bar-container {
        background-color: #2e2e2e !important;
    }

    .Widget_Content_CircleView .widgetBody .carousel .circle-view li .details ul.inline {
        background-color: #4b4b4b !important;
        color: white !important;
    }

    .well, .nav-list, .inner-menu .nav-list li a, .inner-menu .nav-list .nav-header a, ol.recommends>li, .timeline-Widget {
        background-color: #4b4b4b !important;
        color: white !important;
    }

    .timeline-Tweet:hover {
        background-color: #696969 !important;
    }
}
