css:
@-moz-document regexp('(https://mcpedl\\.com).*') {
    /********************************/
    /*Theme Created by @win.epic.fin*/
    /********************************/
     :root {
        --color-000: #000;
        --color-444: #444;
        --color-555: #555;
        --color-666: #666;
        --color-777: #777;
        --color-999: #999;
        --color-aaa: #aaa;
        --color-bbb: #bbb;
        --color-c0c0c0: silver;
        --color-ccc: #ccc;
        --border-color: rgb(15, 0, 12);
        --color-eee: #eee;
        --white: #fff;
        --color-op1: hsla(0, 0%, 100%, 0.1);
        --color-op5: hsla(0, 0%, 100%, 0.5);
        --color-op7: hsla(0, 0%, 100%, 0.7);
        --color-sidebar: hsla(0, 0%, 100%, 0.8);
        --dark-blue: #005580;
        --blue: #08c;
        --light-blue: #3b8dbd;
        --dark-green: #32a852;
        --green: #32a852;
        --yellow: #ff0;
        --red: #f56c6c;
        --light-red: #ffeaea;
        --bg-menu: #26272b;
        --bg-button: #330050;
        --bg-button-hover: #9a03b8;
        --bg-submission: #49afcd;
        --bg-sidebar: #474747;
        --bg-sidebar-top: #400a73;
        --bg-tag: #f2f2f2;
        --bg-secondary: #fff;
        --bg-image-submission: linear-gradient(180deg, #5bc0de, #2f96b4);
        --bg-image-button: linear-gradient(180deg, #62c462, #51a351);
        --bg-skin-image-button: linear-gradient(180deg, #454545, #262626);
        --bg-skin-download-hover: #262626;
        --skin-about: #dff0d8;
        --skin-about-border: #d6e9c6;
        --box-shadow-op05: rgba(0, 0, 0, 0.05);
        --box-shadow-op1: rgba(0, 0, 0, 0.1);
        --box-shadow-op2: rgba(0, 0, 0, 0.2);
        --box-shadow-op25: rgba(0, 0, 0, 0.25);
        --box-shadow-opw2: hsla(0, 0%, 100%, 0.2);
        --box-shadow-opw06: hsla(0, 0%, 100%, 0.06);
        --box-shadow-opw15: hsla(0, 0%, 100%, 0.15);
        --text-shadow: 0;
        --vip-comment-username: #2d730a;
        --vip-comment-border: #38ccc9;
        --vip-comment-bg: #f1fbff;
        --ck-highlight-marker-yellow: #fdfd77;
        --ck-highlight-marker-green: #450086;
        --ck-highlight-marker-pink: #fc7899;
        --ck-highlight-marker-blue: #72ccfd;
        --ck-highlight-pen-red: #e71313;
        --ck-highlight-pen-green: #128a00
    }
    img {
        max-width: 1000px;
        height: auto;
        border-radius: 20px;
    }
    select {
        padding: 0;
        margin: 0;
        background: transparent;
        border-radius: 20px;
    }
    .submission-view.entry {
        padding: 20px;
        border-radius: 2px;
        border: 1px solid #ccc
    }
    .el-button.is-round {
        border-radius: 20px;
        padding: 12px 23px
    }
    .el-button-group>.el-button:first-child:last-child.is-round {
        border-radius: 20px
    }
    .frontend-vip-button[data-v-5d6d0e45] {
        background: #d0ab32;
        font-family: inherit;
        font-size: 14px;
        color: #fff;
        line-height: 25px;
        padding: 4px 14px;
        display: inline-block;
        vertical-align: middle;
        height: 30px;
        margin-right: 10px;
        border-radius: 25px;
    }
    .frontend-login-button[data-v-3f9f7c15] {
        background: linear-gradient(180deg, #62c462, #51a351);
        font-family: inherit;
        font-size: 14px;
        color: #fff;
        line-height: 25px;
        padding: 4px 14px;
        display: inline-block;
        vertical-align: middle;
        height: 30px;
        border-radius: 20px;
    }
    .post--full .copy-submission-details {
        display: inline-block;
        position: absolute;
        right: 13px;
        top: 12px;
        width: 25px;
        height: 30px;
        z-index: 1;
        cursor: pointer;
        border-radius: 20px;
    }
    .logged_in[data-v-afb902fe] {
        margin-bottom: 15px;
        color: var(--white);
    }
    .default-theme .header {
        width: 100%;
        position: sticky;
        top: 0;
        left: 0;
        right: 0;
        background-color: var(--bg-menu);
        box-shadow: 0 0 5px var(--box-shadow-op2), 0 1px 0 var(--box-shadow-opw15);
        z-index: 999
    }
    .main-menu.header .container {
        width: 98.182%;
        max-width: 100%;
        margin: 0 auto;
        display: flex;
        justify-content: space-between
    }
    .main-menu .logo__img {
        min-width: 51px;
        width: 51px;
        display: block;
        border-radius: 5px;
    }
    .container[data-v-3eaf95b8] {
        max-width: 1100px;
        margin: 0 auto;
        position: relative;
        display: flex;
        align-items: flex-start;
        justify-content: space-between;
        padding: 30px
    }
    .default-theme .main {
        position: relative;
        display: flex;
        width: 98.182%;
        max-width: 100%;
        background-color: var(--bg-secondary);
        margin: 0 auto;
        box-shadow: 0 1px 1px var(--box-shadow-op1);
        min-height: 600px
    }
    .default-theme .search__input input {
        width: 100%;
        font-size: 14px;
        background: var(--bg-secondary);
        border: 2px solid var(--border-color);
        padding: 9px 8px 9px 26px;
        color: var(--color-999);
        transition: .3s;
        border-radius: 25px;
    }
    .default-theme .sidebar-actions__btn.bg--blue {
        background: var(--bg-image-submission);
        border-radius: 25px;
    }
}
