css:
@-moz-document url("http://netcity.edu-lesnoy.ru:81/") {
.entry-form.loginbox.appear {
    position: absolute;
    margin-left: 20px;
}
.span-product-name {
    position: absolute !important;
    display: block;
    font-size: 60px!important;
    font-weight: bolder!important;
    width: 500px!important;
    word-break: break-all;
    display: none !important;
}
#schools, #cities, span, input.control-input.col-md-6, .button-login-title {
    width: 200px !important;
}
span.button-login-title {
    margin: 0 !important;
    -webkit-box-shadow: 0 0 0 0 !important;
	background: #434a50 !important;
    color: white !important;
    border-radius: 15px !important;
    padding:0 !important;
    height: 45px;
    line-height: 45px;
    border: 1px solid #434a50 !important;
    font-size: 16px !important;
    box-shadow: 0 0 0 0 !important;

}
.row-center.row-submit {
    position: relative;
}
.control-input {
    margin: 0 15px !important;
}
.sectiontable {
	

}
.entry-form.loginbox.appear {
	left: 30px;
    width: 450px;
    height: fit-content;
    margin: auto;
    top:0;
    bottom:0;
    
}
.img-logo.hidden-xs {
	display: none;
}
.message-form {
	background-color: #00000080 !important;
    backdrop-filter: blur(15px);
}
.tabs-form, .row-center-a {
	display: none !important;
}
.box-form {
	border: 0px!important;
    border-radius: 15px !important;
}
div#message{
	border-radius: 15px !important;

}
html body .centered .entry-form .sectiontable .form-horizontal, .button-login {
	background-color: #00000000;
}

a.button-login.button-login-marker {
    border: 0 !important;
    box-shadow: 0 0 0 0 !important;
    background: #00000000 !important;
    -webkit-box-shadow: none;
    
}
.row-center.row-submit {
    background: #00000000;
    box-shadow: 0 0 0 0 !important;
    border: 0 !important;
}
.row-center.row-submit {
    margin: 0 15px;
    margin-top: 15px;
}
input.control-input.col-md-6 {
background: #434a50 !important;
}
html body .message-form .form-horizontal .select-main span {
    background: none;
    
}
html body .message-form .form-horizontal .select-main span {
	box-shadow: none !important;
}
body {
background: url(https://i.ibb.co/xMssSCQ/glass-window-g3bdebcbb5-1920.jpg) rgba(0,0,0,0.4) !important ;
background-size: cover!important;
    
    background-repeat: no-repeat !important;
    background-attachment: fixed !important;

}
select#schools {
    background: #434a50;
}
div.page-preloader{

background-color: #434a50;

}

.spinner::before {
	display: none !important;
}.text {
	text-transform: capitalize;
    color: white !important;
    display: auto !important;
}

}
