css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mobile.boston.com") {


/* UNIVERSAL
=======================================*/

a {
	color:#2851A2;
	}



/* HEADLINES + TEASES
=======================================*/

.main-news {
	overflow:hidden !important;
	}

.main-news-collapsed-hl img {
	margin-bottom:6px !important;
	}


.main-news .main-news-collapsed-hl {
	margin-top:-1px !important;
	}

.headline,
.main-news-collapsed-hl {
	padding: 13px 8px !important;
	border-bottom:none !important;
	border-top:1px dotted #9e9e9e !important;
	}

.main-news .p, 
.news-block .p {
	color:#272727 !important;
	font:normal .9em/135% Georgia,Times,Times New Roman,serif !important;
	}

.stat-link {
	border:none !important;
	border-top:1px dotted #9e9e9e !important;
	border-bottom:1px dotted #9e9e9e !important;
	font-size:0.9em !important;
	padding: 13px 8px !important;
	}

.stat-link a {
	color:#272727 !important;
	}


.shadow {
	padding:0 !important;
	}

#breaking-news {
	border-top:none !important;
	font-size:1em !important;
	border-bottom: 1px dotted #9E9E9E !important;
	background-color:transparent !important;
	padding-bottom:10px !important;
	}

#breaking-news .br-headline {
	display:block !important;
	float:left !important;
	padding:9px !important;
	text-align:center !important;
	background-color:#B12228 !important;
	color:#fff !important;
	margin:0 6px 0 0!important;
	font-size:0.85em !important;
	}

#breaking-news .br-title {
	font-size:.85em !important;
	margin-top:2px !important;
	}



/* NAVIGATION ELEMENTS
=======================================*/

.header-headline.thumb-header a {
	color:#000 !important;
	padding-right: 18px !important;
	background:transparent url(http://cache.boston.com/universal/site_graphics/caret_h3_trans.gif) no-repeat right 1px !important; 
	}

.logo .section-logo, 
.logo .section-thumb, 
.logo .thumb-header {
	margin-right:7px !important;
	}


#breadcrumb {
	font-size:0.75em !important;
	}

.breadcrumb-box {
	text-transform:none !important;
	color:#666 !important;
	margin:5px 5px 8px !important;
	}

.breadcrumb-box a,
.breadcrumb-box a:visited {
	color:#415FA7 !important;
	}

.links-headline {
	background-color:#f2f2f2 !important;
	color:#000000 !important;
	font-size:0.7em !important;
	font-weight:bold !important;
	margin-top:14px !important;
	padding:10px 8px 8px !important;
	text-transform:uppercase !important;
	}

.section-headline {
	padding:13px 8px !important;
	}

.section-headline.border {
	border-top:4px solid #002878 !important;
	}

.simple-link-box {
	border:none !important;
	border-top:1px solid #ccc !important;
	padding:8px 0 10px !important;
	}

.lb-left {
	padding:0 6px 5px 5px !important;
	}

.lb-right {
	border-left:1px solid #ccc !important;
	padding: 0 5px 5px 6px !important;
	}

.box-navi {
	overflow:hidden;
	}

.box-navi .simple-link-box a {
	color:#000 !important;
	}

.box-navi .fiftyfifty {
	margin-top:-1px !important;
	}


.view-all {
	font-size:0.8em !important;
	}

.view-all a {
	display: block;
	background-color: #ebf4fb !important;
	border:1px solid #b7dcff !important; 
	text-align:center !important; 
	color:#272727 !important; 
	font-weight:bold !important; 
	padding:12px !important; 
	margin: 5px 5px 8px !important;
	text-transform:capitalize !important;
	-webkit-border-radius: 5px !important;
	-moz-border-radius: 5px !important;
	}

.view-all a {
	text-transform:capitalize !important;
	}


/* HOMEPAGE
=======================================*/

.temperature {
	font-weight:bold !important;
	letter-spacing:-0.03em !important;
	}

.front-weather img {
	margin-left:5px !important;
	}


/* ARTICLE PAGES
=======================================*/

#article-block .authordate {
	font-size:0.8em !important;
	margin:4px 0 0 0 !important;
	}

#article-block p {
	font:normal 1em/130% Georgia,Times,Times New Roman,serif !important;
	line-height:130%;
	}

#article-block blockquote {
	line-height:130% !important;
	margin:0 10% !important;
	padding:0 0 0 15px !important;
	border-left:2px solid #999 !important;
	}



/* TOP NAV 
=======================================*/

.logo, 
.frontpage-logo {
	border-top:4px solid #002878 !important;
	border-bottom:1px solid #cacaca !important;
	padding:7px 8px 7px 5px !important;
	}

#top-navi {
	border-bottom:none !important;
	background-color:transparent !important;
	}

#top-navi a:visited {
	color:#2851A2 !important;
	}

#top-navi .top-link {
	background-color:#F2F2F2 !important;
	border-bottom:1px solid #CACACA !important;
	} 

#top-navi .top-link.selected {
	border-bottom:none !important;
	background-color:#fff !important;
	}

#top-navi .selected a {
	color:#000 !important;
	}


/* LIVE SCORES
=======================================*/

.in-game-team-name {
	color:#000 !important;
	font-weight:normal !important;
	}

.in-game {
	background-color:transparent !important;
	margin-bottom:5px !important;
	}

.more-sport-data {
	border-top:1px solid #cacaca !important;
	padding:5px !important;
	background-color:#f2f2f2 !important;
	margin-bottom:10px !important;
	}


.in-game img {
	border:1px solid #999 !important;
	position:relative !important;
	top:10px !important;
	margin:0 4px 0 8px !important;
	}






/* ADS 
=======================================*/

.top-banner,
.bottom-banner {
	border-top:1px dotted #9E9E9E !important;
	padding:10px 0 5px !important;
	} 



.top-banner.top-rule {
	margin-top:13px !important;
	} 




/* BOTTOM NAV 
=======================================*/

.bottom-navi {
	padding:20px 12px !important;
	background-color:#F2F2F2 !important;
	margin: 10px 0 !important;
	}

.bottom-navi .container div {
	border-bottom:none !important;
	padding:10px 0 10px 8px !important;
	line-height:100% !important;
	height:auto !important;
	}

.bottom-navi .container {
	border-top:1px solid #999 !important;
	border-bottom:none !important;
	}

.bottom-navi .fiftyfifty {
	border-bottom:1px solid #999 !important;
	}




/* FOOTER 
=======================================*/

#search-block, 
#font-size, 
#copyright, 
#copy-links {
	background-color:transparent !important;
	text-align:center !important;
	} 


#copyright {
	font-size:0.7em !important;
	}

#copy-links a {
	padding:5px 8px !important;
	border:1px solid #ccc !important;
	}

#copy-links {
	margin: 6px 0 10px !important;
	font-size:0.75em !important;
	}


}
