css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("reddit.com") {
/* comment box */
.comment {
	-moz-border-radius-topleft: 7px!important;
	-webkit-border-top-left-radius: 7px!important;
	border:1px solid #bbbcbf !important;
	border-right:0!important;
	background-color:#ffffff !important;
	margin-bottom:-1px!important;
	margin-top:-7px!important;
}
.commentarea .infobar{
	margin-bottom:1em!important;
}
.comment .comment{
	background-color:#F0F0F0 !important;
	margin-right:0px!important;
	margin-left:-1ex!important;
}
.comment .comment .comment{
	background-color:#ffffff !important;
}
.comment .comment .comment .comment{
	background-color:#F0F0F0 !important;
}
.comment .comment .comment .comment .comment{
	background-color:#ffffff !important;
}
.comment .comment .comment .comment .comment .comment{
	background-color:#F0F0F0 !important;
}
.comment .comment .comment .comment .comment .comment .comment{
	background-color:#ffffff !important;
}
.comment .comment .comment .comment .comment .comment .comment .comment{
	background-color:#F0F0F0 !important;
}
.comment .comment .comment .comment .comment .comment .comment .comment .comment{
	background-color:#ffffff !important;
}
/* wide */

body{
	max-width:9999px !important;
}
.organic-listing,#search,.side,.bottommenu,.gadget,.sidecontentbox,.sidebox,.titlebox{
	display:none!important;
}
div.content{
	border:1px solid #cccccc !important;
	padding:0!important;
}
div.content a{
	color:#336699 !important;
}
div.content a.admin{
	color:#ff0000 !important;
}
a.thumbnail{
	border:1px solid #000!important;
}
div.link{
	padding-bottom:1px!important;
	border-bottom:1px dotted #cccccc !important;
	margin:0!important;
	margin-bottom:5px!important;
}
div.md{
	max-width:9999px !important;
}
div.md a:visited{
	color:#713192 !important;
}
div.md blockquote{
	border-left:6px solid #cccccc !important;
}
div.md blockquote p{
	color:#555555 !important;
	font:12px Verdana,sans-serif !important;
}
div.md li{
	font:13px/1.2 Georgia,serif !important;
}
div.md p{
	font:13px/1.6 Georgia,serif !important;
}
div.sitetable textarea{
	height:300px !important;
}
html{
	overflow:-moz-scrollbars-vertical;/* force vertical scrollbar in Firefox */
	overflow-x:auto;
}
input{
	font:12px /1.6 Verdana,sans-serif !important;
}
p.title a.title{
	color:#336699 !important;
	font:15px Georgia,serif !important;
}
p.title a.title:visited{
	color:#653399 !important;
}
.domain{
	font-size:.6em!important;
	float:right;
	margin-right:2px;
}
textarea{
	background:#eeeeee !important;
	border:1px solid #cccccc !important;
	font:12px /1.6 Verdana,sans-serif !important;
	width:100%!important;
}
ul.flat-list a{
	font-size:11px !important;
	font-weight:normal !important;
}
ul.flat-list li.first a:visited{
	color:#653399 !important;
}
.downvotes{
	color:#666666 !important;
}
.footer *,.footer-parent,.commentarea h1{
	display:none !important;
}
.nextprev{
	margin-left:3ex!important;
}
.friend{
	background:#dff4d5;
}
.infobar{
	background-color:#eeeeee !important;
	border:0!important;
	border-bottom:1px solid #cccccc !important;
	margin:0!important;
	color:#666666 !important;
	font-size:11px !important;
}
.lightdrop,.tagline{
	font-size:11px !important;
}
.linkinfo,.linkinfo *{
	color:#666666 !important;
}
.link.last-clicked{
	border:none !important;
	background-color: #f0f0f0!important;
}
.pagename{
	font-variant:normal !important;
}
.pagename a{
	color:#336699 !important;
	text-shadow:0 0 .2em #ffffff,0 0 .2em #ffffff,0 0 .2em #ffffff !important;
}
.pagename a:hover{
	text-decoration:none !important;
	text-shadow:0 0 .6em #ffffff,0 0 .6em #ffffff,0 0 .6em #ffffff !important;
}
.side{
	background:none !important;
}
.submitter{
	background-color:#ffff88;
}
.titlebox,.titlebox h1,.titlebox .karma{
	color:#666666 !important;
	font-size:12px !important;
}
.usertext-edit{
	display:block!important;
	width:98%!important;
}
.usertext-edit textarea{
	border:1px solid #cccccc !important;
	height:80px !important;
}
#sr-bar .selected a{
	font-weight:bold !important;
}
#sr-bar a{
	color:#336699 !important;
}

.arrow{
	height:8px!important;
	width:100%!important;
}
.comment .arrow{
	margin-bottom:1em!important;
	padding-left:1.5ex!important;
}
.comment .midcol{
	padding-right:1ex!important;
}
.arrow.downmod{
	background-image:url(data:image/gif;base64,R0lGODlhDwAIAIABAJSU/////yH5BAEAAAEALAAAAAAPAAgAAAIThI8ZyRbf0JsRzNuuVnq73lldAQA7)!important;
	background-position:50% 50%!important;
}
.arrow.down{
	background-image:url(data:image/gif;base64,R0lGODlhDwAIAIABALe3t////yH5BAEAAAEALAAAAAAPAAgAAAIThI8ZyRbf0JsRzNuuVnq73lldAQA7)!important;
	background-position:50% 50%!important;
}
.arrow.up{
	background-image:url(data:image/gif;base64,R0lGODlhDwAIAIABALe3t////yH5BAEAAAEALAAAAAAPAAgAAAITjB+Ai+C8nIRGWmhzzLpynnxfAQA7)!important;
	background-position:50% 50%!important;
}
.arrow.upmod{
	background-image:url(data:image/gif;base64,R0lGODlhDwAIAIABAP+LYP///yH5BAEAAAEALAAAAAAPAAgAAAITjB+Ai+C8nIRGWmhzzLpynnxfAQA7)!important;
	background-position:50% 50%!important;
}
.rank{
	margin-left:0ex!important;
	text-align:center!important;
	float: right!important;
}
.midcol{
	margin-left:0px!important;
}

}
