css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gondal.de") {
div#gondal *,
div#balkentext {
text-shadow:#000 0px 0px 10px;
}

a img[id^=nmenu],
div.boxoben a,
div#char_left a[id^=link],
div.banker a,
img#neueware,
input#neueware,
img[src*=bag_],
img[id^=button],
button {
opacity:0.8 !important;
}

a img[id^=nmenu]:hover,
div.boxoben a:hover,
div#char_left a[id^=link]:hover,
div.banker a:hover,
img#neueware:hover,
input#neueware:hover,
img[src*=bag_]:hover,
img[id^=button]:hover,
button:hover {
opacity:1 !important;
}
}
