css:
@-moz-document domain("github.com") {
	:root {
		--animation-secs: 5s;
		--color1: #FF0000;
      	--color2: #FFA500;
      	--color3: #FFFF00;
      	--color4: #008000;
      	--color5: #0000FF;
      	--color6: #800080;
	}

	@keyframes rainbow {
		0% {
			color: var(--color1);
		}
		16% {
			color: var(--color2);
		}
		32% {
			color: var(--color3);
		}
		48% {
			color: var(--color4);
		}
		64% {
			color: var(--color5);
		}
		80% {
			color: var(--color6);
		}
		100% {
			color: var(--color1);
		}
	}

	@keyframes rainbow-background {
		0% {
			background: var(--color1);
		}
		16% {
			background: var(--color2);
		}
		32% {
			background: var(--color3);
		}
		48% {
			background: var(--color4);
		}
		64% {
			background: var(--color5);
		}
		80% {
			background: var(--color6);
		}
		100% {
			background: var(--color1);
		}
	}

	@keyframes rainbow-filter {
		0% {
			filter: hue-rotate(0deg);
		}
		16% {
			filter: hue-rotate(51deg);
		}
		32% {
			filter: hue-rotate(102deg);
		}
		48% {
			filter: hue-rotate(153deg);
		}
		64% {
			filter: hue-rotate(204deg);
		}
		80% {
			filter: hue-rotate(255deg);
		}
		100% {
			filter: hue-rotate(360deg);
		}
	}

	.octicon-mark-github > path,
	.octicon-pencil > path,
	.octicon-history > path,
 	.octicon-check > path,
  	.octicon-eye > path,
	.octicon-star,
	.octicon-law > path,
	.octicon-book:not(.UnderlineNav-octicon) > path,
	.octicon-repo-forked:not(.color-fg-muted) > path,
	.octicon-pin > path,
	.octicon-link > path,
	.text-bold:not(.css-truncate):not(.px-2):not([data-view-component="true"]),
	a[itemprop="name codeRepository"],
	.previous_page:not(.disabled),
	.next_page:not(.disabled),
	.flex-shrink-0 > path,
	.octicon-git-pull-request:not(.UnderlineNav-octicon) > path,
	.octicon-issue-opened:not(.UnderlineNav-octicon) > path,
	.octicon-code-of-conduct > path,
	.octicon-container > path,
	.octicon-repo > path,
	.octicon-repo-push > path,
	.octicon-comment-discussion > path,
	.octicon-flame > path{
		animation: infinite rainbow var(--animation-secs);
	}

	.ActionList-item-visual > .octicon-repo > path {
		animation: none;
	}

	.btn-primary,
  	.ActionList-item--navActive::after {
		animation: infinite rainbow-background var(--animation-secs);
	}

	.ContributionCalendar-day,
	.Label--success,
  	.octicon-star-fill > path,
  	.Progress-item:not(.color-bg-success-emphasis),
  	.State--merged,
  	.State--closed,
  	.State--open,
	.topic-tag,
	.current {
		animation: infinite rainbow-filter var(--animation-secs);
	}
}
