css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("sunniforum.com") {

/*background color change*/
.page{
background:white!important;
}

.page>div:first-child{
width:980px!important;
}

.orangebar{
display:none!important;
}

/*forum left vertical column removed*/

.forum_left{
display:none!important;
}

/*forum right vertical column removed*/

.forum_right{
display:none!important;
}

/*thread option background modified*/

.tcat_left, .tcat_right,.vbmenu_control2, .vbmenu_control2 a:link, .vbmenu_control2 a:hover, .vbmenu_control2 a:active, .vbmenu_control2 a:visited{
background:none!important;
height:22px!important;
line-height:22px!important;
color:white!important;
}

a:link, a:visited{
color:#024769!important;
}

.tcat_bg{
background:#005B9A!important;
}


/*user info background modified*/

.pbbar{
background:none!important;
border-style:solid;
border-width:1px;
border-color:#F0F2F2!important;
font-size:10px!important;
height:13px!important;
padding-top:2px!important;
}


/*post text color*/

.alt1pbt{
color:#000000!important;
border:0px!important;
}

/*post date background*/
.thead{
background:#A8B1B8!important;
color:black!important;
}

/*username color change*/
.bigusername{
color:#000!important;
}

/*modifying header*/


.navfirst, .navitem{
background:none!important;
border-right:1px solid black!important;
padding-left:12px!important;
height:20px!important;
line-height:20px!important;

}

.navlast{
display:none!important;
}
.navigationbar{
height:20px!important;
background:#005B9A!important;
}
.navigation{
margin-top:0px!important;
margin-bottom:0px!important;
}
div.header{
background:#005B9A!important;
border-top:1px solid black!important;
border-bottom:1px solid black!important;
}

.navfirst a:link, .navfirst a:hover, .navfirst a:active, .navfirst a:visited, .navitem a:link, .navitem a:hover, .navitem a:active, .navitem a:visited {
color:white!important;

}


/*remove registration bar*/
.regbar{
display:none!important;
}

/*remove the second column
body>div:first-child+div+div>div:first-child>div:first-child>table:first-child>tbody:first-child>tr:first-child>td:first-child+td+td+td+td+td+td>form:first-child>table:first-child+table>tbody:first-child+tbody>tr>td:first-child+td{
display:none!important;
}

*/

/*post list*/
.alt1, .alt1active, .alt2, .alt2Active {
background:#FAFAFA!important;
}

img[src*="rating_1.gif"], img[src*="rating_2.gif"], img[src*="rating_3.gif"], img[src*="rating_4.gif"], img[src*="rating_5.gif"],
img[src*="tag.png"],
img[src*="paperclip.gif"],
img[src*="firstnew.gif"], img[src*="lastpost.gif"],
img[src*="multipage.gif"],
img[src*="gosearch.gif"]{
display:none!important;
}





/*removing 1st column*/
table#threadslist>tbody:first-child+tbody>tr>td:first-child, table#threadslist>tbody>tr>td:first-child{
display:none!important;
}

/*removing 2nd column*/
table#threadslist>tbody:first-child+tbody>tr>td:first-child+td,
table#threadslist>tbody>tr>td:first-child+td{
display:none!important;
}

/*removing (view) column*/

table#threadslist>tbody:first-child+tbody>tr>td:first-child+td+td+td+td+td, table#threadslist>tbody>tr>td:first-child+td+td+td+td+td{
display:none!important;
}
/***********/

table#threadslist>tbody:first-child>tr:first-child{
display:none!important;
}

table#threadslist>tbody:first-child>tr:first-child>td:first-child+td{
width:50px!important;
}


table#threadslist>tbody:first-child+tbody>tr>td>div.smallfont, table#threadslist>tbody>tr>td:first-child+td+td>div.smallfont{
color:grey!important;
}

/*moving number of replies to left*/
table#threadslist>tbody:first-child+tbody>tr{
position:relative!important;
background:#FAFAFA!important;
}

table#threadslist>tbody:first-child+tbody>tr>td:first-child+td+td+td+td{
position:relative!important;
left:-938px!important;
top:9px!important;
width:28px!important;
height:20px!important;
float:left!important;
background:#EBF0F1!important;
font-size:13px!important;
color:white!important;
border:1px solid #9FAFC2!important;
font-weight:bold!important;
}

/*for search pages*/
table#threadslist>tbody>tr>td:first-child+td+td+td+td{
position:relative!important;
left:-818px!important;
top:9px!important;
width:28px!important;
height:20px!important;
float:left!important;
background:#EBF0F1!important;
font-size:13px!important;
color:white!important;
border:1px solid #9FAFC2!important;
font-weight:bold!important;
}

table#threadslist>tbody>tr:first-child+tr{
display:none!important;
}

table#threadslist>tbody>tr{
background:#FAFAFA!important;

}

div.headerbar>a:after{
content:"sunniforum"!important;
font:60px bold!important;
color:#000000!important;
}
div.headerbar>a>img{
display:none!important;
}

.qsl{
background:#D3E0F3!important;
height:80px!important;
}
td#countupie{
color:black!important;
}

div.headerbar{
border:0px solid #9FAFC2!important;
height:50px!important;
background:#D3E0F3!important;
padding-top:0px!important;
width:300px!important;

}

.qsandlogin{
background:none!important;
height:50px!important;
background:#D3E0F3!important;
padding-top:0px!important;
color:black!important;

}
div.qsandlogin>h4{
font-size:10px!important;
color:black!important;
}

div.qsandlogin>div>a,div.qsandlogin>a{
color:black!important;
}

div.smallfont_panel{
color:black!important;
}

div.smallfont_panel>strong>a{
color:navy!important;
}

body{
width:980px!important;
background:#00275E!important;
margin:0pt auto!important;
}

div.page>div:first-child>div:first-child{
padding:0px 0px!important;

}

.header{
background:#000!important;
height:22px!important;
position:relative!important;
top:100px!important;
}

.headerimg{
display:none!important;
}

table#threadslist>tbody:first-child+tbody>tr>td:first-child+td+td, table#threadslist>tbody>tr>td:first-child+td+td{
padding-left:50px!important;
width:100%!important;
}



table#threadslist.tborder .inlineimg{
position:relative!important;
left:135px!important;
top:10px!important;
float:right!important;
}

.search-quick, .bginput2{
border:solid 1px!important;
background:none!important;
}

td.forum_bg>table:first-child{
padding-top:0px!important;
}

td.alt2pbt+td{
position:relative!important;
left:-140px!important;
top:30px!important;
float:left!important;
padding:10px 10px 10px 10px!important;
width:750px!important;
background:#F7F7F1!important;
}
td.alt2pbt>div+table, td.alt2pbt>div+table+div{
position:relative!important;
left:800px!important;
top:5px!important;
}

td.forum_bg>div#posts>div>table>tbody>tr:first-child+tr {
background:#F0F2F2!important;

}

td.forum_bg>div>table>tbody>tr[valign="top"]{
background:#F0F2F2!important;
}




td.alt2pbt>div:first-child{
float:left!important;
padding-left:30px!important;
}

td.alt2pbt[width="175"]{
width:155px!important;
background:#F0F2F2!important;

}

td.alt2pbb, td.alt1pbb{
padding-top:30px!important;
background:#F0F2F2!important;
}

center{
display:none!important;
}

div.headerbar>table{
display:none!important;
}

td.sidebar{
display:none!important;
}

div.footer_bg{
display:none!important;
}

.bc a:link, .bc a:hover, .bc a:visited, .bc a:active {
color:navy!important;
}

/*td.alt2{
background:#F0F2F2!important;
border:solid 1px #A8B1B8!important;
}

table#threadslist>tbody>tr>td.alt2{
background:#FAFAFA!important;
border:0px!important;
}*/

td.forum_bg>table>tbody>tr>td>div{
display:none!important;
}

.forum_bg{
background:none!important;
padding-top:0px!important;
}
}
