@-moz-document domain("neopets.com") {







/********************************************************************/



/* HTML Tag Styles **************************************************/



/********************************************************************/







	A:Link, A:Visited {



		color: #4F6C95 !important;



	}







	A:Hover {



		color: #4B9292 !important;



	}







/********************************************************************/



/* Foundation IDs ***************************************************/



/********************************************************************/







	#main {



		border-left: 1px solid #747474 !important;



		border-right: 1px solid #747474 !important;



	}

.brand-mamabar{display:none;}







	#header {



		background-color: #275881 !important;



		background-image: url("http://img695.imageshack.us/img695/5572/headeryj.png") !important;



	}







	#footer {



		background-color: #275881 !important;



		background-image: url("http://img695.imageshack.us/img695/6326/footy.png") !important;



	}











/********************************************************************/



/* Header Stuff *****************************************************/



/********************************************************************/







/* Navigation.  Duh.  */



	ul.dropdown {



		border: 1px solid #000000 !important;



		background-color: #4F6C95 !important;



	}







	LI:hover UL.dropdown A, LI.over UL.dropdown A {



		color: #000000 !important;



	}







	LI:hover UL.dropdown A:Hover, LI.over UL.dropdown A:hover {



		color: #C6CFE1 !important;



	}







/* Event Icons */



	.eventIcon, .user {



		color: #FFFFFF !important;



	}







	.eventIcon A:Link, .eventIcon A:Hover, .eventIcon A:Visited,



	.user A:Link, .user A:Hover, .user A:Visited {



		color: #9AB9D7 !important;



	}







/* Clock */



	#nst {



		color: #000000 !important;



	}











/********************************************************************/



/* Sidebar Classes **************************************************/



/********************************************************************/







	.sidebarModule {



		border: 1px solid #CC6600 !important;



	}







	.sidebarTable {



		border: 1px solid #CC6600 !important;



	}







	.sidebarHeader {



		background-color:#CC6600!important;



		border-bottom: 1px solid #CC6600!important;



	}







	.sidebarHeader A:Link, .sidebarHeader A:Hover, .sidebarHeader A:Visited {



		color: #000000 !important;



	}











/********************************************************************/



/* Content Classes **************************************************/



/********************************************************************/







/* We need a few styles to make a module work */



	.contentModuleHeader {



		background-color: #000000 !important;



		color: #FFFFFF !important;



	}







	.contentModuleHeader A:Link, .contentModuleHeader A:Hover, .contentModuleHeader A:Visited {



		color: #C5C5C5 !important;



	}







	.moreLink {



		border: 1px solid #8F8F8F !important;



	}







	.contentModuleHeaderAlt {



		background-color: #2F3E5B !important;



		color: #E6E9F0 !important;



	}







	.contentModuleHeaderAlt A:Link, .contentModuleHeaderAlt A:Hover, .contentModuleHeaderAlt A:Visited {



		color: #C6CFE1 !important;



	}







	.moreLinkAlt {



		border: 1px solid #536FA2 !important;



	}











/********************************************************************/



/* Footer Classes ***************************************************/



/********************************************************************/







	.footerForm {



		color: #FFFFFF !important;



	}







	.copyright, .copyright A:Link, .copyright A:Hover, .copyright A:Visited {



		color: #FFFFFF !important;



	}











/********************************************************************/



/* Misc Classes of Doom *********************************************/



/********************************************************************/







/* Ad banners.  No comment */



	#ban {



		background-color: #747474 !important;



		background-image: url("http://img706.imageshack.us/img706/9964/admaker.png") !important;



	}







	.marqueeSlot, .marqueeSlot A:Link, .marqueeSlot A:Hover, .marqueeSlot A:Visited {



		background-color: #4B6492 !important;



		color: #E6E9F0 !important;



	}







	.marqueeSlotOn, .marqueeSlotOn A:Link, .marqueeSlotOn A:Hover, .marqueeSlotOn A:Visited {



		background-color: #2F3E5B !important;



		color: #E6E9F0 !important;



	}











/********************************************************************/



/* Erweitert*********************************************************/



/********************************************************************/







        #ban {



                border: 1px !important;



        }



    



        #header {



                border: 1px !important;



        }











}
