css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("einskanal.net") {
img.banner
{
   display: block!important;
   margin-left: auto!important;
   margin-right: auto!important;
}

div.site_name
{
   font-family: Verdana, Tahoma, sans-serif!important;
   font-size: 250%!important;
   font-weight: bold!important;
   text-align: center!important;
   margin-top: 1em
}

div.site_slogan
{
   font-family: Verdana, Tahoma, sans-serif!important;
   font-weight: normal!important;
   font-size: 125%!important;
   font-style: italic!important;
   text-align: center
}

div.desuchan_version
{
   position: fixed !important!important;
   bottom: 0 !important!important;
   left: 0 !important!important;
   right: 0!important;
   padding: 1em!important;
   background: inherit!important;
   font-size: 80%!important;
   font-style: italic!important;
   font-weight: bold!important;
}

div.desuchan_version .desu
{
   color: red!important;
}

div.desuchan_version .chan
{
   color: green!important;
}

div.hidden_thread
{
   margin-bottom: 10px!important;
   clear: both!important;
}

span.hidden_thread
{
   background: #ccccee!important;
   border-color: #313370!important;
   border: 2px solid #313370!important;
   padding: 3px!important;
}

div.postform_info_notice
{
   background: #aaaacc!important;
   border: 3px solid #313370!important;
   padding: 3px!important;
   color: #000000!important;
   font-weight: bold!important;
}

div.postform_info_error
{
   background: #ff7777!important;
   border: 3px solid #ff3333!important;
   padding: 3px!important;
   color: #000000!important;
   font-weight: bold!important;
}

body
{
   background: #eeeeee!important;
   font-family: sans-serif!important;
   font-size: 80%!important;
color:#000!important;
}

ul.boardlist { list-style: none!important; padding-left: 0px!important; margin: 0px!important; }
ul.boardlist li { margin: 0px!important; }

ul.boardlist li:hover
{
   background: #dddddd!important;
}

ul.boardlist li a
{
   display: block!important;
   width: 100%!important;
}

span.board_newposts, li.board_newposts
{
   background: #aaaacc!important;
}

li.board_newposts:hover
{
   background: #bbbbdd!important;
}

span.nav_group_button
{
   float: right!important;
   font-size: 80%!important;
   font-weight: normal!important;
   padding-left: 4px!important;
   padding-right: 4px!important;
   margin: 0px 0px!important;
   background: #383a80!important;
   color: #7075ff!important;
   border: 1px solid #434699!important;
   cursor: pointer!important;
}

span.nav_group_button:hover { background: #4f52b3!important; }

h2 a:link { color: #aeaeff!important; }
h2 a:visited { color: #ffaeae!important; }
h1 { color: #000000!important; text-align: center!important; }
h2 { margin: 0px!important; padding-left: 2px!important; color: #ffffff!important; background: #313370!important; font-family: Verdana, Tahoma, Arial, sans-serif!important; font-weight: bold!important; font-size: 100%!important; }
h3 { text-align: center!important; background: #313370!important; }

.invalidquotelink
{
   color:#666622!important;
   font-style: italic!important;
}

a.quotelink:active, a.quotelink:link, a.quotelink:visited
{
   color:#000000!important;
   text-decoration: none!important;
}

a.quotelink:hover
{
   font-weight:bold!important;
}

.sage
{
   color: #ff0000!important;
   font-weight: bold!important;
}

table.postform td.label
{
   color: #ffffff!important;
   background: #313370!important;
   font-weight: bold!important;
   text-align: left!important;
}

table.postform td.label_highlighted
{
   color: #ffffff!important;
   background: #ff3333!important;
   font-weight: bold!important;
   text-align: left!important;
}

.nav_group
{
   color: #ffffff!important;
   background: #313370!important;
   font-weight: bold!important;
}

img { border: none!important; }
a:link { color: #222299!important; text-decoration:underline!important;}
a:visited { color: #992222!important; text-decoration:underline!important;}

.postername
{
   color: #3333cc!important;
   font-weight: bold
}

.tripcode
{
   color: #337733!important;
   font-weight: bold
}

.authority_admin
{
   color: #ff0000!important;
   font-weight: bold
}

.authority_mod
{
   color: #bb00bb!important;
   font-weight: bold
}

.postsubject
{
   color: #cc3333!important;
   font-size: large!important;
   font-weight: bold
}

.ban_mark
{
   color: #ff0000!important;
   font-weight: bold
}

.thread
{
	color:#000!important;
}

.omittedinfo
{
   color: #666666!important;
}

.postbody
{
   color: #000000!important;
}

.replybody
{
   color: #000000!important;
   float: left!important;
   padding: 0px 20px 0px 40px!important;
   position: relative!important;
   overflow: hidden!important;
}

.replybody blockquote
{
   margin: 0px!important;
}

.postreply
{
   float: left!important;
   background: #aaaacc!important;
   color:#000000!important;
}

.postreply_highlighted
{
   float: left!important;
   background: #aaaacc!important;
   border: 2px dashed #444488!important;
}

.fileinfo
{
   font-style: italic!important;
   font-size: 11px!important;
}

.quote
{
   color: #007777!important;
}

span.spoiler
{
   color: #000000!important;
   background: #000000!important;
}

span.spoiler:hover
{
   color: #ffffff!important;
   background: #000000!important;
}

span.buttonrow
{
   vertical-align: bottom!important;
}

.aa
{
   font-family: IPAMonaPGothic, Mona, 'MS PGothic', YOzFontAA97!important;
   text-align: left!important;
}

.file_thread
{
   float: left!important;
   margin: 5px 20px 20px 5px!important;
   background: #dddddd!important;
   border: #aaaaaa 1px solid!important;
   text-align: left!important;
}

.file_reply
{
   float: left!important;
   margin: 5px 20px 20px 5px!important;
   background: #9999bb!important;
   border: #aaaaff 1px solid!important;
   text-align: left!important;
}

.progressbar
{
	position: relative!important;
	border: 2px solid #9999bb!important;
	background: #dddddd!important;
	height: 2em!important;
}

.progressbar div
{
	position: absolute!important;
	top: 0px!important;
	left: 0px!important;
	height: 100%!important;
	background-color: #9999bb!important;
}

.progressbar div p
{
   white-space: nowrap!important;
   display: block!important;
   height: 100%!important;
   margin: 0!important;
   padding: 0 1em!important;
   line-height: 2em!important;
   font-weight: bold!important;
}

div.box_title
{
   padding: 1px 10px!important;
   background-color: #313370!important;
   font-weight: bold!important;
   color: #ffffff!important;
   -moz-border-radius-topleft: 10px!important;
   -webkit-border-radius-topleft: 10px!important;
   -moz-border-radius-topright: 10px!important;
   -webkit-border-radius-topright: 10px!important;
}

div.menu_title
{
    color: #ffffff!important;
    background: #313370!important;
    font-size: 150%!important;
    font-weight: bold!important;
 }
		 
div.box
{
   margin-bottom: 15px!important;
   padding: 5px 10px!important;
   background-color: #dddddd!important;
   border-left: solid 1px #313370!important;
   border-right: solid 1px #313370!important;
   border-bottom: solid 1px #313370!important;
   -moz-border-radius-bottomleft: 10px!important;
   -webkit-border-radius-bottomleft: 10px!important;
   -moz-border-radius-bottomright: 10px!important;
   -webkit-border-radius-bottomright: 10px!important;
}

div.newsbody
{
            margin-bottom: 15px!important;
            padding: 5px 10px!important;
            background-color: #dddddd!important;
            border-left: solid 1px #313370!important;
            border-right: solid 1px #313370!important;
            border-bottom: solid 1px #313370!important;
            -moz-border-radius-bottomleft: 10px!important;
            -webkit-border-radius-bottomleft: 10px!important;
            -moz-border-radius-bottomright: 10px!important;
            -webkit-border-radius-bottomright: 10px!important;
}
}
