@namespace url(http://www.w3.org/1999/xhtml);

/*--------------------------------------*/
/*     C S S   b y   H i r u m a n      */
/*--------------------------------------*/

@-moz-document url-prefix(http://images.google.com),
url-prefix(http://www.google.com),
url-prefix(http://news.google.com),
url-prefix(http://translate.google.com),
url-prefix(http://books.google.com),
url-prefix(http://blogsearch.google.com),
url-prefix(https://www.google.com/accounts),
url-prefix(http://www.google.com*/ig*){

/*------------Common Tags------------*/
body{background-color:#000!important;}
*{border-color:#444!important;font-family:'Segoe UI';font-size:12px;}
div,span,body,a{color:#111!important;}
cite{color:#333!important;}
a{color:#666!important;}
a:hover{color:#fff!important;}
b{color:#555 !important;}
img[src*="logo."]{border:0!important;}
#gbar,span,td,div,label,center{background-color:#000 !important;color:#ddd !important;}
/*------------------------------------*/

#sbl a{font-size:10px!important;}

/*----------Google Search-------------*/
tr.gac_b,.gac_b{background-color:transparent!important;}
.gac_b td:hover{cursor:pointer;}
/*------------------------------------*/

/*-----------NavBar Fixes-------------*/
.csb,.ss,#logo span,.tbi div,#tbp,#rptglbl{background-image:url(http://img191.imageshack.us/img191/359/navlogo7.png) !important;}
/*------------------------------------*/

/*--------Sponsored Links Fixes-------*/
h2{font-size:14px!important;}
#rhsline{border-left:1px solid #333!important;}
#mboxEnd{border:none !important;background-color:#000!important;}
/*------------------------------------*/

/*-------Advanced Seach Pages---------*/
.page-title{background-color:#bbb !important;color:#555 !important;}
tr[bgcolor$=bbcced] > td{background-color:#333 !important;}
td[bgcolor$=d5ddf3]{background-color:#bbb !important;border-top:1px solid #fff !important;}
td > table[style*="3366cc"]{border-top:1px solid #fff !important;}
.block{border:3px solid #333 !important;}
.qbuilder-env{border-color:#333 !important;}
table[style*="3366cc"]{border-top:1px solid #fff !important;}
/*------------------------------------*/

/*---Google Reader---*/
#search-input{-moz-appearance:none!important;}
.message-area-inner,.message-area-inner *{background-color:#555!important;}
li.unselectable{background-color:#000!important;}
.subscribe-button,.section-button,#lhn-selectors .selector-icon,.interruption-minimize,.scroll-tree .icon,.scroll-tree .toggle,.add-comment-link,.bundle-container .bundle-show-details-toggle,.bundle-feed-icon{background-image:url(http://img402.imageshack.us/img402/759/1007736847lhnsprite.png)!important;}
.toggle,.folder-toggle{background-color:transparent!important;}
.selector:hover,a.link:hover,a.link:hover span,a.link:hover div{background-color:#222!important;}
#directory-selector,#directory-selector *,#chrome-header,#chrome-header *{background-color:#444!important;}
#chrome-lhn-toggle-icon{border-left-color:#111!important;border-top-color:#111!important;border-bottom-color:#111!important;}
#chrome-lhn-toggle:hover,#chrome-lhn-toggle:hover #chrome-lhn-toggle-icon{background-color:#444!important;border-left-color:#444!important;border-right-color:#111!important;border-top-color:#444!important;border-bottom-color:#444!important;}
.lhn-hidden #chrome-lhn-toggle-icon{border-left-color:#444!important;border-right-color:#111!important;border-top-color:#111!important;border-bottom-color:#111!important;}
#chrome-lhn-toggle:hover,#chrome-lhn-toggle:hover .lhn-hidden #chrome-lhn-toggle-icon{border-left-color:#111!important;border-right-color:#444!important;border-top-color:#444!important;border-bottom-color:#444!important;}
#chrome-lhn-toggle,#directory-box,.tab-group,.tab-group .unselectable{background-color:#111!important;}
#directory-contents .tab-header-selected{background-color:#000!important;}
.bundle-container{background-color:#111!important;border-color:#444!important;}
.bundle-show-details-link{background:none!important;}
.bundle-container th,.bundle-container .bundle-icon{background-color:#444!important;}
/*-------------------*/

/*---Google News---*/
.basic-title,.basic-title *{background-color:#111!important;}
.nub-item{border-color:#000!important;}
/*-----------------*/

/*---Google Books---*/
.hp_header,.goog-tooltip,.goog-tooltip *{background-color:#222!important;}
/*------------------*/

/*---Google Translate---*/
#dict_head,#dict_head *,#autotrans,#autotrans *{background-color:#222!important;}
/*----------------------*/

/*Google Accounts Login box*/
.form-noindent{background-color:#000!important;}
td[bgcolor="#e8eefa"],
.loginBox,
.loginBox table,
.loginBox td,
.loginBox div,
.form-noindent div,
.form-noindent td,
.gaia{background-color:#333!important;}
img[src*="google_transparent.gif"]{padding-left:61px;width:61px!important;background-image:url(http://img704.imageshack.us/img704/4282/googletransparent.gif)!important;}
.btn nobr{color:#000!important;}
/*-------------------------*/

/*Logo border fix*/
img[src*="logo"],.logo,#logo{border:0!important;}

/*Search Logo*/
img[src*="nav_logo7.png"]{padding-left: 164px !important;height: 106px !important;width: 0 !important;background-image: url('http://img191.imageshack.us/img191/359/navlogo7.png') !important;}

/*News Logo*/
img[src$="news.gif"]{padding-left:170px!important;width:0px!important;background-image:url(http://img687.imageshack.us/img687/7891/newsv.gif);height:40px!important;}

/*Advanced Web Search Logo*/
img[src$="logo_sm.gif"]{padding-left:150px!important;height:55px!important;width:0!important;background-image: url('http://img196.imageshack.us/img196/2193/logosmx.gif') !important;}

/*Google Search Logo*/
img[src$="logo.gif"]{padding-left:276px!important;height:110px!important;width:0!important;background-image: url('http://img638.imageshack.us/img638/6953/logou.gif') !important;}

/*Google Images Logo*/
img[src$="/images_logo_lg.gif"]{padding-left:276px!important;height:110px!important;width:0!important;background-image: url('http://img97.imageshack.us/img97/9449/imgslogo.gif') !important;}

/*Google Reader Logo*/
a#logo-container h1#logo{background-image:url(http://img190.imageshack.us/img190/8286/756311971readerlogoen.gif)!important;}

/*Google Books Logo*/
img[src*="books_logo.gif"]{background-image:url(http://img268.imageshack.us/img268/5993/bookslogo.gif)!important;padding-left:174px!important;height:40px!important;}

/*Google Translate Logo*/
img[src*="translate_logo.gif"]{background-image:url(http://img715.imageshack.us/img715/1336/translatelogo.gif)!important;padding-left:205px!important;height:40px!important;}

/*Google Docs Logo*/
img[src*="docs_logo.gif"]{background-image:url(http://img64.imageshack.us/img64/5296/docslogo.gif)!important;padding-left:160px!important;height:40px!important;}

/*--DropDown menu--*/
.rpop,.tl-sect,.tl-sel,.tl{background-color:transparent!important;}
#gbi a.gb2:hover{background-color:#555!important;}
/*-----------------*/

}

/*--------------Even More page-----------*/
@-moz-document url-prefix(http://www.google.com/intl){
li a span{background-image:url(http://img138.imageshack.us/img138/2797/90699660.png) !important;}
h2{color:#eee!important;background-color:#222!important;}
*{border-color:#555!important;}
#logo img{padding-left:143px!important;height:59px!important;background-image:url(http://img193.imageshack.us/img193/5528/googlesm.gif)!important;width:0!important;}
}
