@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://www.google),
               url-prefix(http://images.google) {
html {
background: #303231 url("http://nsa11.casimages.com/img/2009/12/14/091214014038332426.jpg") center no-repeat !important;
background-attachment: fixed !important;
margin:0 !important; }
body {
-moz-border-radius: 8px !important;
border: 2px solid #707070 !important;
width: 84% !important;
min-width: 815px !important;
padding: 8px 1.2% 0 1.2% !important;
margin: 51px auto 30px auto !important; }
input[type=text], input[name=q] {
