#res li:hover a.l:after { color:#0F3AB8; }
#ssb {
border:1px solid #6B90DA;
margin-right:1.1em !important;
overflow:hidden; }
#prs{ padding:4px 5px 0 10px !important; }
#ImgContent table { width: 99%!important; }
td[id^="tDataText"] { color: #bbb !important; }
td[id^="tDataText"] font[color="#008000"] { color: #777 !important; }
a#logo, a#logo[title*="Google Home"] { font-size: 0 !important; }
