height: 159px !important;
padding-left: 450px !important;
/* background-image: url("http://nsm02.casimages.com/img/2009/12/14/091214054352844575052534.png") !important; */ }
#logo > img, #logo span, img[src$="logo_sm.gif"], img[src$="google_sm.gif"], img[src$="logo_google_suggest_sm.gif"], img[src$="images_logo.gif"], img[src$="nav_logo6.png"] {
width: 0 !important;
height: 51px !important;
padding-left: 135px !important;
background-position: 0 0 !important;
top: 0 !important;
background-image: url("http://nsm02.casimages.com/img/2009/12/14/091214054352844575052535.png") !important;
margin: 34px 12px 0 15px !important; }
img[src$="images_res.gif"], img[src$="logo_sm.gif"] {
width: 0 !important;
height: 61px !important;
padding-left: 136px !important; }
#sft #logo {
width: 135px !important;
height: 51px !important; }
#sft #logo img {
margin-top: 4px !important;
margin-left: 0px !important;
width: 400px !important;
height: 51px !important;
padding-left: 168px;
background: url("http://nsm02.casimages.com/img/2009/12/14/091214054352844575052535.png") no-repeat !important; }
#bsf, table.ft.t.bb.bt, #footer {
background: none !important;
margin-top: -40px !important; } 
#nav > tbody > tr > td span { background: none !important; }
#nav, #nav .cur, #navbar a { font-size: 120% !important; }
#nav { margin-top: -30px !important; }
#nav .cur { margin-top: -40px !important; }
#nav td, #navbar * { vertical-align: bottom !important; }
#nav td { padding: 0.15em !important; }
#navbar img { visibility: hidden !important; }
#navbar * {
background-image: none !important;
padding: 0.10em !important; }
#navbar .i { font-size: 1.2em !important; }
#navbar a { font-size: 0.9em !important; }
form p table[width="100%"][cellspacing="0"][cellpadding="0"][border="0"] {
border: 3px solid #585858 !important;
margin: 3px !important; }
form p table[width="100%"][cellspacing="0"][cellpadding="0"][border="0"] td[width="175"] { border-right: 1em solid #585858 !important; }
#gsr #res .g .s { max-width:100% !important; }
/* Update fade-in */
div#bsf, table#brs { display:none !important; }
div#ssb, #fctr, #sbl, span.fade, #guser, #fctr,#ghead,#pmocntr,#sbl,#tba,#tbe,.fade, td.k { background-color: transparent !important; }
/* thank's Silver the Paladin for Update fade-in */
/*********************************************************/
table.gac_m {
background-color: #000 !important;
opacity: 0.9 !important; }
.gac_b { background-color: #46534A !important; }
#rptgl span, .tl-sect div.tl b {
color: #fff !important; }
#rpsp.rpop, .tbo #tbd {
background-color: #333 !important;
border: 0 !important;
margin: 10px 0 0 50px !important;
padding: 8px 15px 0 5px !important;
-moz-border-radius: 4px !important; }
body { opacity: 0.65 !important; }
body, .t, table [bgcolor="#e5ecf9"], table [bgcolor="#d5ddf3"], .hd, td.ttb, td.rsb, td.bts, h4, .page-title, td#headerText, #ssb, span[style*="background: rgb(255, 255, 153)"], td[bgcolor="#d5ddf3"], tr[bgcolor="#cbdced"], tr[bgcolor="#bbcced"], tr[bgcolor="#ffffff"], tr[bgcolor="#E5ECF9"], form p table[width="100%"][cellspacing="0"][cellpadding="0"][border="0"], form p table[width="100%"][cellspacing="0"][cellpadding="0"][border="0"] td[width="175"], table[bgcolor="#ffffff"], tr[bgcolor="#ffffff"], tr[bgcolor="#cbdced"], h4, p > table > tbody > tr:first-child {
background: #111 !important; }
.gbh, #navbar br, form p table td[bgcolor="#cbdced"], span #botstuff {
display: none !important; }
body, input[type=text], input[name=q], a:link, a, p, a:visited, a:hover, #gb a:hover, #guser a:hover, td.tc a:hover, td a:hover, p.a:hover, .g:hover h3.r a, .g h3.r a, a:link, .w, #prs a:visited, #prs a:active, .q:active, .q:visited, #navbar .i, #navbar a, #navbar a:hover {
