#gbar b.gb1, #gbar a.gb1, a.gb2, #gbar a.gb1:hover, a.gb2:hover, #gbar .gb1, .gb2, #gbar a.gb3, #gbar a.gb3:hover, #gbar .gb3, #gb a, #guser a, td.tc a, td a, p.a {
font-family: Verdana, Tahoma, Helvetica, Times; }
body, input[type=text], input[name=q], .g:hover h3.r a, .g h3.r a, #navbar .i, #navbar a, #rptgl span, #guser a, #guser a:hover, .g div.s cite, #res li:hover a.l:after  {
font-weight: bold !important; }
#gb a, #gb a:hover, #guser a, #guser a:hover, #ssb {
-moz-border-radius: 4px !important; }
body, input[type=text], input[name=q] { color: #f5f5f5 !important; }
#gbar a.gb1, a.gb2, #gbar a.gb3, #gb a, #guser a, td.tc a, td a, p.a, .g:hover h3.r a { color: #a7a7a8 !important; }
a:link, a, p, #gb a:hover, #guser a:hover, td.tc a:hover, td a:hover, p.a:hover, .g h3.r a, #gbar a.gb1:hover, a.gb2:hover, #gbar a.gb3:hover, .g, div.e, font.med, #res p a.p, div.s, font.p, #navbar .i, div, td, font[color="#000000"], td[id^="tDataText"] b, .w, #prs a:visited, #prs a:active, .q:active, .q:visited { color: #fff !important; }
font[color="#224499"] { color: #116DA6 !important; }
#gbar a.gb1, a.gb2, #gbar a.gb3 { font-weight: 700 !important; }
#gbar b.gb1, #gb a, td.tc a, td a, p.a { font-weight: 700; }
font.med, #res p a.p, div.s, #tads a, #tads li { font-size: 12px !important; }
font.p { font-size: 13px; }
img[style*="border: 1px solid"], td[id^="tDataText"] b, td[id^="tDataText"], td[id^="tDataText"] font[color="#008000"], .bb, .bt, div#footer {
