css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("torrentfreak.com"),domain("torrentfreak.eu") 
{
a:link{color: #FF3C78!important}
a:visited{color: #FF3C78!important}
a:hover{color: #6E6E6E!important}
a.dsq-reply-link{color: #999!important}
a[href^="#comment-"]{color: #CCC!important}



.dsq-full-comment{width:90%!important}
.dsq-request-user-logout{padding-right:30px!important}
#comments,#disqus_thread,#dsq-content{width:99%!important}

body{background:#000 url(data:image/jpeg;base64,) repeat-x 0px 0px!important}
html{background:#000!important}
#author-wrapper{background:#333!important;width:90%!important}
#comment,[class="comment"],blockquote,#featured{background:#222!important;padding: 4px 4px 4px 4px !important}
.comment{margin-bottom:-5px!important}
#wrapper,#content-wrapper,#dsq-toolbar-dropdown-wrap{background-image:none!important}
#logo{margin: 7px 0px 0px!important}
#introduction{margin: -17px 0px 0px!important}
#searchform{margin: 0px 0px 0px!important}
#masthead{margin-bottom:3px!important}
#main-content,article,#read-comments,#post-supp{width:950px!important}
#content-wrapper{margin-left:10px!important}
.button.news{margin-left:-25px!important}
#main-content,article{padding-right:10px!important}
#main-content{padding: 8px 0px 0px!important}
#searchform{width:386px!important}
p,textarea,tr,#comment{color:#fff!important}
blockquote{color:#ccc!important}
td,.dsq-request-user-info,.dsq-global-toolbar-likes,.dsq-like-activity,.dsq-options,.dsq-missing-permissions p{background:#222!important}
.dsq-request-user-info{padding-right:3px!important;margin-right:-3px!important}
th{background:#444!important}
h4{color:#ccc!important}
#post-info{float: right!important;margin: -7px 0px 15px 10px!important;width:125px!important}
#related,h3,#main,#trackbacks,#rss-info,#featured,#supp-content,tfoot,#other-news,.dsq-comment-show-thread,.dsq-global-toolbar-dropdown-container,.alt_ad,font[size="1"]{display:none!important}
#latest-news{padding-left: 15px!important}
#share,#disqus_thread,.dsq-missing-permissions p{border:none!important}
#dsq-global-toolbar{height:12px!important}
.dsq-like-activity{margin-top:2px!important}
.dsq-tt{margin-top:0px!important}
.dsq-embed-message.dsq-show{background:#222!important;-webkit-box-shadow: #444 0px 1px 0px inset !important;border-top: 1px solid #444 !important;color: #fff!important}
button,.dsq-like-activity{color:#FFF!important}
.dsq-popup.dsq-button,.dsq-button{-webkit-box-shadow: #000 0px 1px 0px inset, rgba(0, 0, 0, 0.199219) 0px 1px 2px !important}
}

@-moz-document url-prefix(http://torrentfreak.disqus.com),url-prefix(http://www.torrentfreak.disqus.com)
{
#content{background:#000!important}
#footer{background: #000!important;border:0!important;width:100%!important;padding:0!important;-webkit-border-radius:none!important;-moz-border-radius:none!important;border-bottom-left-radius: 0px 0px!important;border-bottom-right-radius: 0px 0px!important}

.total-comments{background:url(data:image/png;base64,%3D%3D) 12px 8px !important; background-repeat:no-repeat!important}
.total-likes{background:url(data:image/png;base64,%3D) 12px 8px !important;background-repeat:no-repeat!important}
.total-people{background:url(data:image/png;base64,%3D) 12px 8px !important;background-repeat:no-repeat!important}
}

@-moz-document url-prefix(http://torrentfreak.com/author/)
{
#main-content,article{margin-left:10px!important}
}
