css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(https://www.zhihu.com) {
  .Sticky.AppHeader .QuestionHeader-title {
      display: none;
  }
  .Card-header.RelatedCommodities-title, .Question-sideColumn.Question-sideColumn--sticky {
      display: none;
  }
  .Question-mainColumn {
      width: 1032px !important;
  }
  .AppHeader-inner>a {
      display: none;
  }
  .QuestionHeader-main {
      width: 1032px !important;
  }
  .Button.SearchBar-askButton {
      display: none;
  }
  .AppHeader-userInfo .AppHeader-notifications {
      display: none;
  }
  .AppHeader-userInfo .AppHeader-messages {
      display: none;
  }
  .GlobalSideBar {
      display: none;
  }
  .Topstory-mainColumn {
      width: 1000px !important;
  }
  .TopstoryPageHeader-main svg {
      display: none;
  }
}
