css:
@-moz-document domain("amazon.co.jp") {
div.tabular-buybox-container
{
    grid-template-columns: none !important;
}
div[id="tabular-buybox"] div[class*="text"],
div[id="tabular-buybox"] div[class*="text"] span|div,
div#usedDeliveryBlockContainer + div div.a-row
{
    line-height : 1em !important;
    overflow : visible !important;
    position : inherit !Important;
    max-width : 100% !important;
    max-height : 100% !important;
    margin : 0px !important;
    padding : 0px !important;
    z-index : 0 !important;
    opacity : 1 !important;
    height : 100% !important;
    width : 100% !important;
}
div[id="tabular-buybox"] div[class*="text"]:nth-child(n+6) span,
div[id="tabular-buybox"] div[class*="text"] *
{
    white-space : normal !important;
}
div[id="tabular-buybox"] div[class*="text"]:nth-child(1),
div#usedDeliveryBlockContainer + div div.a-row
{
    border : 2px dotted silver;
    border-radius : 5px;
    padding : 8px 5px !important;
    margin : 5px 0 !important;
    display : block !important;
}
div[id="tabular-buybox"] span,
div#usedDeliveryBlockContainer + div div.a-row
{
    font-size : 8px !important;
    color : gray !important;
}

div[id*="tabular-buybox-"]
{
    display : none !important;
}
div[id="tabular-buybox"] div[class*="text"] *:not(#non-exist-id),
div#usedDeliveryBlockContainer + div div.a-row:not(#non-exist-id),
#merchant-info a:not(#non-exist-id),
#merchant-info a span:not(#non-exist-id)
{
    font-size : 21px !important;
    text-shadow : 2px 2px 3px lightgray !important;
    letter-spacing : -0.5px !Important;
    line-height : 1.2em !important;
}
div[id="tabular-buybox"] div[class*="text"]:nth-child(1):not(#non-exist-id),
div#usedDeliveryBlockContainer + div div.a-row:not(#non-exist-id),
#merchant-info a:not(#non-exist-id),
#merchant-info a span:not(#non-exist-id)
{
    font-size : 21px !important;
    padding : 10px 6px 11px 12px !important;
    display : block !important;
}

div[id*="tabular"] a[href*="/gp/help/customer"]:not(#non-exist-id),
#merchant-info a[href*="/gp/help/customer"]:not(#non-exist-id)
{
    color : deepskyblue;
    font-weight : bolder !Important;
    letter-spacing : 0px !Important;
    line-height : 1.3em !important;
}
#sellerProfileTriggerId:not(#non-exist-id),
div[id*="tabular"] a[href*="/gp/help/seller"]:not(#non-exist-id),
#merchant-info a[href*="/gp/help/seller"]:not(#non-exist-id),
#merchant-info a[href*="/sp?marketplaceID="]:not(#non-exist-id)
{
    color : #ff0018e0 !important;
    text-decoration : underline !Important;
    line-height : 1.3em !important;
}
div#usedDeliveryBlockContainer + div div.a-row *:not(#non-exist-id)
{
    line-height : 1.3em !important;
}

#centerCol #merchant-info a
{
    text-align : center !important;
    padding : 13px 9px !important;
    background-color : #fafafa;
}
}

@-moz-document domain("amazon.co.jp") {
table[id*="tabular"] *
{
    line-height : 1em !important;
    overflow : visible !important;
    position : inherit !Important;
    max-width : 100% !important;
    max-height : 100% !important;
    margin : 0px !important;
    padding : 0px !important;
    z-index : 0 !important;
    opacity : 1 !important;
    height : 100% !important;
    width : 100% !important;
}
table[id*="tabular"]  tr,
#merchant-info a
{
    border : 2px dotted silver;
    border-radius : 5px;
    padding : 8px 5px !important;
    margin : 5px 0 !important;
    display : block !important;
}

table[id*="tabular"] td,
table[id*="tabular"] .a-truncate
{
    display : block !important;
}
table[id*="tabular"] tr td:nth-child(1) *
{
    font-size : 8px !important;
    color : gray !important;
}
table[id*="tabular"] .a-truncate-cut
{
    display : none !important;
}
table[id*="tabular"] .a-truncate-full:not(#non-exist-id),
#merchant-info a:not(#non-exist-id)
{
    font-size : 1.7em !important;
    padding : 6px 5px 3px !important;
    line-height : 1.2em !important;
    text-shadow : 2px 2px 3px lightgray !important;
    letter-spacing : -0.5px !Important;
}

table[id*="tabular"] .a-truncate-full
{
    color : dimgray;
}
table[id*="tabular"] a[href*="/gp/help/customer"]:not(#non-exist-id),
#merchant-info a[href*="/gp/help/customer"]:not(#non-exist-id)
{
    color : deepskyblue;
    font-weight : bolder !Important;
    letter-spacing : 0px !Important;
    line-height : 1.3em !important;
}
#sellerProfileTriggerId:not(#non-exist-id),
table[id*="tabular"] a[href*="/gp/help/seller"]:not(#non-exist-id),
#merchant-info a[href*="/gp/help/seller"]:not(#non-exist-id),
#merchant-info a[href*="/sp?marketplaceID="]:not(#non-exist-id)
{
    color : #ff0018e0 !important;
    text-decoration : underline !Important;
    line-height : 1.3em !important;
}
#centerCol #merchant-info a
{
    text-align : center !important;
    padding : 13px 9px !important;
    background-color : #fafafa;
}
}
