css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("isohunt.com") {

[valign="top"] [style="margin: 0pt;"] > td:first-child > *:not(ul):not(p)
 {display: none !important;}
[valign="top"] [style="margin: 0pt;"] > td:first-child > p
 {width: 200px !important;}
[valign="top"] [style="margin: 0pt;"] > td:first-child + td
 {padding-left: 10px !important;}
ul > li > a:not(.postreply)
 {display: none !important;}
ul > li
 {font-size: 0 !important; color: transparent !important;}

/* Remove stuff */
#ihOps, #facebook
 {display: none !important;}
/* Remove Blue Rectangle from Header */
.bodyline > tbody:first-child > tr > td > table > tbody > tr:first-child > td {background-color: transparent !important;}
/* Filter Dropdown Menu */
#ihType, #ihType option {color:#000!important;}

/*** t31os Code ***/

/****** Redefine standard HTML tags ******/
html {width:100%!important;margin:0!important;padding:0!important;background:#333!important}
body {width:auto!important;margin:0!important;padding:0 10px 0 10px!important;background:#111!important;}
a:link,a:active,a:visited,a.postlink, td.row4 a {color:#5c1!important;text-decoration:none!important}
a:hover, a.postlink:hover, td.row4 a:hover {color:#af6!important;background:inherit!important;text-decoration:underline!important}
hr {color:#000!important;border-top:1px solid #fc3!important;border-bottom:0!important;opacity:0.5!important;}
*:not([class]){border:0!important} /** All elements without a class / no border **/
li {color:#fff!important}
th {background:#333!important;}
* {font-size:11px!important;}
blockquote {background:#333!important;}

/****** Header Columns ******/
td.ihLogo {background:#222!important;width:auto!important;float:left!important;display:table-cell!important;padding:0 0 0 0!important}
td[style="background: rgb(0, 51, 102) none repeat scroll 0% 0%; 
-moz-background-clip: -moz-initial; -moz-background-origin: 
-moz-initial; -moz-background-inline-policy: -moz-initial;"][width="60%"]
{background:#222!important;width:auto!important;float:right!important;display:table-cell!important;padding:20px 50px 0 0!important;}

/****** Replace logo ******/
img[src*="img/logo.gif"]{
background: url("data:image/gif;base64,") no-repeat !important;
background-position:0 0!important;
width: 0px!important;
height: 70px !important;
padding-left:0!important;
padding-right:200px!important;
padding-top:0!important;
padding-bottom:0!important;
margin:0 0 10px 30px!important;
}

/****** Content Columns ******/
td[width="84%"] {margin:0!important;background:#222!important;width:85%!important;}
td.row4 {width:15%!important;background:#222!important;color:#fff!important}

/****** Information bar ******/
.navbkg {width:100%!important;background:#111!important;border:0!important;color:#fff!important;text-align:center!important;}

/****** Navigation Menu - Top left ******/
#nav li ul {background-color: #333!important;border:1px solid #000!important;white-space:nowrap!important;width:140px!important;padding:5px 0 5px 0!important;}
#nav li ul a:hover{background: #666!important;padding:1px 125px 1px 15px!important;color:#7c3!important;}

/****** Classes & IDs ******/
.topBar, .forumline {width:100%!important;margin:0!important;padding:0!important;border:0!important;background:inherit!important}
.bodyline {border:1px solid #000!important;background:#222!important}
.row1 {background:#181818!important;color:#ccc!important;padding:6px!important;border:0!important}
.row2 {background:#181818!important;color:#ccc!important;padding:6px!important;border:0!important}
.row3 {background:#333!important;color:#ccc!important;padding:6px!important;border:0!important}
.ihHead {background:#333!important;color:#fff!important;padding:6px 0 6px 0!important;border:0!important} 
.pager * td {white-space:nowrap!important;border:0!important;padding:0!important;margin:0!important;width:auto!important;}
.dirs .inner {background-color: #000!important;}
.dirs {top: 30px!important;left: 20%!important;width: 350px!important;}
.fileRows, .fileRowsRight {border:0!important;}
.fileRows, .fileRowsRight, p  {color:#666!important;}
.cat {background:#333!important;padding:0 0 0 10px!important;}
.postdetails{color:#ccc!important;}
.gensmall {color:#7c3!important;}
.genmed {color:#fc3!important;}
.releases {border-bottom:1px solid #a70!important;background:#111!important;color:inherit!important}
.spacerow {background:#333!important;}
.postbody {color:#aaa!important;}
.quote {background:#333!important;color:#ccc!important;border:0!important}
.comments {border:1px solid #000!important;padding:4px!important;background:#000!important;}
.commentsBody {background:#222!important;border:0!important;}
.commentBody1 {border-top:1px solid #000!important;}
.spacerowcomment {background:#7c3!important;}
.commentsFooter {border-top:1px solid #000!important;}
.commentsDetails {border:0!important;background:#333!important;color:#fc3!important;padding:4px!important;text-align:center!important;}
#torrent_details {background:#333!important;color:#ccc!important;-moz-border-radius-topleft: 4px !important;-moz-border-radius-topright: 4px !important;-moz-border-radius-bottomleft: 4px !important;-moz-border-radius-bottomright: 4px !important;}
#detailsTabs li:last-child {display:none!important}
#detailsTabs li {padding:10px 0 0 15px!important;}
#detailsTabs a {background:#333!important;-moz-border-radius-topleft: 4px !important;-moz-border-radius-topright: 4px !important;}
#detailsTabs a span {background:none!important;}
#detailsTabs a:hover {background:#111!important;}

/****** Special selectors, for those awkward to get areas ******/

td:not([class]){color:#fff!important}
.forumline td:not([class]) {background:#111!important;}
span[style="color: rgb(0, 0, 0);"], div[style="float: left; text-align: left;"]{color:#fff!important}
font[color="#CC0000"]{color:#270!important}
a[href*="http://isohunt.com/forum/viewtopic.php?t="] br {display:none!important}
td[align="left"]:not([class]) {display:block!important}
iframe[src*="http://digg.com/tools/diggthis"] {opacity:0.2!important;float:right!important}
iframe:hover[src*="http://digg.com/tools/diggthis"] {opacity:0.4!important;}


/****** Block ads etc ******/
[href*="ads."], [src="ads.widgetbucks.com"], #beacon_152,
#ad, .top_ad_image, #top_ad_msft, iframe[src*="/js/"],
#hottest_ad_msft, p[style="margin: 8px; text-align: center;"], 
span[style="color: rgb(255, 255, 255);"],
a[href*="http://feeds.feedburner.com"], span[style="font-size: 9px; color: rgb(180, 180, 180);"],
a[href*="https://isohunt.com/a/adclick.php"], .ab, .SL_zone {display:none!important}

}
