css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("xiaonei.com") {body a, body a:hover, #userProfile h4 {
  color:black !important;
}

#header #tagline, #header #utility {
  height:25px !important;
background:URL("http://farm1.static.flickr.com/171/486058964_9346308d90.jpg?v=0") repeat-x scroll 0% !important;
}
#header #tagline a, #header #utility a, #header #utility {
  color:black !important;
}
#header #utility a:hover {
  background:transparent !important;
}

#navigation {
  height:27px !important;
  border-bottom:0px !important;
  margin-bottom:2px !important;
background:URL("http://farm1.static.flickr.com/221/486089539_ee32e7f812.jpg?v=0") repeat-X scroll 0% !important;
}
#navigation ul {
  padding-top:4px !important;
}
#navigation a:hover {
  background:transparent !important;
}

#userPicture a:hover img, #talk .thumbpic a:hover img {
  border:1px solid #3B5998 !important;
}

#content {
  border-bottom:1px solid #939393 !important;
  border-left:1px solid #939393 !important;
  border-right:1px solid #939393 !important;
}

.inputbutton, .inputsubmit, .subbutton, .canbutton, #starttalkBtn {
  background:#DDDDDD !important;
  color:#202020 !important;
  border:0px !important;
}

.article a:hover {
  background: #3B5998 !important;
  color: #ffffff !important;
  text-decoration: none !important;
}

#mymore a.ppm:hover, #talk a:hover,
#userActions a:hover, #mymenu a:hover {
  background:#2179E5 !important;
  color:#FFFFFF !important;
}

.announcement, #welcome, #permalink, #footer {
  display:none !important;
}

.box h3 {
background:URL("http://farm1.static.flickr.com/169/486058960_05c01e4b20.jpg?v=0") repeat-X scroll 0% !important;
  border-top:1px solid #DDDDDD !important;
  color:#000000 !important;
}
}
