css:
@-moz-document url-prefix("https://frgp.cvg.utn.edu.ar/") {
:root {
	--primary: white;
	--secondary: cyan;
	--tertiary: #222222;
	--light: #555555;
	--link: DeepSkyBlue;
	--danger: red;
	--dark: #111111;
	--borde: 20px;
}

/* OTROS */
 .region_main_settings_menu_proxy {
	background-color: transparent;
}
body {
	background-color: var(--dark);
	color: var(--primary);
}

/* ICONOS */
.fa-star, .fa-star-o {
	color: yellow !important;
}
.icon {
	color: var(--primary);
}
a, .btn-link {
	color: var(--link);
}

/* NAVBAR */
 .navbar {
	background-color: var(--dark) !important;
	color: var(--primary) !important;
}
.navbar-light .navbar-brand, .navbar-light .navbar-brand, .navbar-bootswatch .navbar-brand {
	color: var(--primary);
}

.nav-tabs .nav-link.active, .nav-tabs .nav-item.show .nav-link {
	background-color: var(--light);
	color: var(--primary);
}

/* MAIN */
 #region-main {
	background-color: var(--tertiary);
	border-radius: var(--borde);
}

/* NO LEIDOS */
 .forumpost.unread .row.header, .path-course-view .unread, span.unread {
	background-color: var(--danger);
	border-radius: var(--borde);
}
.forumpost.unread .row.header, .path-course-view .unread, span.unread > a {
	color: var(--primary);
	margin-right: 10px;
	font-weight: bold;
}

/* BOTONES */
 .btn {
	border-radius: var(--borde);
}
.btn-light {
	background-color: var(--dark);
	color: var(--primary);
}
.bg-gray {
	background-color: var(--tertiary) !important;
}

/* DRAWER */
[data-region="drawer"] {
	background-color: var(--dark);
}
.list-group-item.active {
	border-radius: var(--borde);
}

/* DRAWER ITEM */
 .list-group-item {
	background-color: var(--dark);
	color: var(--primary);
}

/* CARD */
 .card {
	background-color: var(--dark);
}
.card-body {
	background-color: var(--tertiary);
	border-radius: var(--borde);
}

/* TABLA */
 .table {
	color: var(--primary);
}
}
