css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("last.fm") {
#fauxHeader,
#lastfmLogo,
#primaryNav .nav-link:hover, 
#profileLinks .divider a,
#loginLink .profile-link,
#loginLink .profile-link strong,
#idBadgerUser,
#idBadgerDropper,
#siteSearchBox,
#siteSearchSubmit,
#headerLangToggle img.globe_icon {
    background-image: url(http://i45.tinypic.com/24deykw.png) !important;
}
#idBadger {
    margin-left: 4px !important;
    position: relative !important;
}

#idBadgerUser {
    position: relative !important;
    display: -moz-inline-box !important;
    display: inline-block !important;
    background-position: 0 -360px !important;
    background-repeat: no-repeat !important;
    font-size: 11px !important;
    height: 11px !important;
    line-height: 11px !important;
    padding: 5px 7px 7px 23px !important;
    color: #eee !important;
    font-weight: bold !important;
    text-decoration: none !important;
    text-shadow: #870802 0 1px 1px !important;
}

#idBadgerUser:hover {
    color: #fff !important;
}

#idBadgerUser img {
    display: block !important;
    position: absolute !important;
    top: 3px !important;
    left: 3px !important;
    height: 16px !important;
    width: 16px !important;
    -webkit-border-radius: 2px !important;
    -moz-border-radius: 2px !important;
}


#idBadgerDropper {
    display: -moz-inline-box !important;
    display: inline-block !important;
    width: 21px !important;
    background-position: 0 -420px !important;
    background-repeat: no-repeat !important;
    font-size: 11px !important;
    height: 11px !important;
    line-height: 11px !important;
    padding: 5px 0 7px 0 !important;
    text-decoration: none !important;
    text-indent: -9999px !important;
}

#idBadgerDropper span {
    visibility: hidden !important;
}

#idBadgerDropper:hover {
    background-position: -21px -420px !important;
}

#idBadger ul {
    display: none !important;
}

#idBadgerDropDown {
    background: #00519f !important;
    display: block !important;
    border: 1px solid #000 !important;
    -webkit-border-radius: 3px !important;
    -moz-border-radius: 3px !important;
    -webkit-box-shadow: rgba(0, 0, 0, 0.3) 0px 2px 2px !important;
    -moz-box-shadow: rgba(0, 0, 0, 0.3) 0px 2px 2px !important;
}

#idBadgerDropDown a {
    display: block !important;
    font-size: 11px !important;
    line-height: 22px !important;
    padding: 0 7px !important;
    color: #eee !important;
    text-decoration: none !important;
    cursor: pointer !important;
}

#idBadgerDropDown a:hover {
    background: #004282 !important;
    color: #fff !important;
    text-decoration: none !important;
    cursor: pointer !important;
}

* html #idBadgerDropDown a {
    display: inline-block !important;
}
span.calSheet span.month {
    background: #0d64b9 !important;
    border: 1px solid #0d64b9 !important;
    color: #fff !important;
    text-transform: uppercase !important;
    line-height: 1.1em !important;
    font-weight: bold !important;
    text-align: center !important;
}
#secondaryNavigation ul {
    overflow: hidden !important;
    padding: 1px 0 0 0 !important;
    border-top: 1px solid #ccc !important;
}

#secondaryNavigation li {
    display: block !important;
}

#secondaryNavigation li a {
    display: block !important;
    width: 94px !important;
    margin: -1px 0 0 0 !important;
    padding: 4px 5px 5px 10px !important;
    text-decoration: none !important;
    background: url(http://cdn.last.fm/flatness/secondary_nav_bg.png) left top repeat-x #ededed !important;
    border: 1px solid #ccc !important;
    color: #000 !important;
}
#secondaryNavigation li.first a {
    border-top: 0 !important;
}

#secondaryNavigation li a:hover,
#secondaryNavigation li.current a {
    color: #0054a6 !important;
    background: #fff !important;
}
#secondaryNavigation li.current a {
    border-right-color: #fff !important;
}
#page h2.heading {
    margin: 15px -15px 2px -15px !important;
    padding: 0 !important;
    color: #0054a6 !important;
    font-size: 18px !important;
    line-height: 13px !important;
    border-top: 1px dotted #ccc !important;
}
.lfmBlack #page h2.heading {
    color: #1b1b1b !important;
}

#page h2.heading .h2Wrapper {
    display: block !important;
    padding: 15px !important;
}

#page h2.heading .h2Wrapper a {
    text-decoration: none !important;
}
#page h2.heading .h2Wrapper a:hover {
    text-decoration: underline !important;
}
#page h2.heading .h2Wrapper a {
    color: #176bbd !important;
}
.lfmBlack #page h2.heading .h2Wrapper a {
    color: #1b1b1b !important;
}

#page div.fiflufi div.leftCol h2 small a,
#page div.fixed div.leftCol h2 small a {
    color: #0054a6 !important;
}

div.fiflufi div.leftCol hr {
    clear: both !important;
    height: 2px !important;
    margin: 15px -15px 10px -15px !important;
    border: 0 !important;
    border-top: 1px solid #ccc !important;
    background: #efefef !important;
    display: none !important;
}

#page div.fiflufi div.rightCol h2.heading,
#page div.fixed div.rightCol h2.heading {
    margin: 15px 0 10px 0 !important;
    padding: 0 !important;
    font-size: 13px !important;
    line-height: 12px !important;
}

#page div.fiflufi div.rightCol h2.heading .h2Wrapper,
#page div.fixed div.rightCol h2.heading .h2Wrapper {
    padding: 7px 0 0 0 !important;
}
#LastFooter {
display: none !important;
    font-size: 11px;
    text-align: left;
    line-height: 1.181818em;
    margin: 10px 0 0 110px;
    color: #000;
    overflow: hidden;
}
}
