css:
@-moz-document url-prefix("https://stos.eti.pg.gda.pl/") {
html, body {
    background-color: #333333;
    color: #f9fcff;
    font-family: Trebuchet MS, sans-serif;
}

select, input[type=button] {
    background-color: #ffffff;
	border: 1px gray solid;
    border-radius: 6px;
}

#topbar {
    display: flex;
    justify-content: space-between;
    align-items: center;
    height: 30px;
    background-color: #141414;
    color: #f9fcff;
}

#topbar div {
    position: static !important;
}

#topbar-time {
	width: auto;    
}

#topbar-menu {
	display: flex;
    align-items: center;
}

#topbar-lang {
    padding: 0 5px;
}

li a:hover{
    text-decoration: underline !important;
    background-color: #141414 !important;
}

.topmenu > li > a:hover {
    background-color: red;
}


.menublock {
    border: 1px solid black;
    border-radius: 6px;
    background-color: #121212;
    height: auto;
    margin-bottom: 15px;
}

span.menuitem {
    margin-top: 2px;
    line-height: 15px;
}

span.menuitem a {
    color: #f9fcff;
    text-decoration: none;
}

span.menuitem a:hover {
    text-decoration: underline;
    background-color: #333333;
}

.menuitem:first-of-type {
    margin-top: 8px;
}

#rightside {
    margin-top: 10px;
    left: 220px;
    background-color: #333333;
}

#leftside {
    margin-top: 10px;
    width: 220px;
    height: 220px;
    background-color: #333333;
}

#editframe {
    border-radius: 6px;
}

#content {
    padding: 10px 10px 20px 10px;
}

#filebox {
	margin-left: 15px;  
    margin-top: 18px;
}

.menuhead {
    color: #f9fcff;
    line-height: 25px;
    margin-bottom: 6px;
}

.loginedit {
    width: 85%;
    margin: 3px auto;
    line-height: 25px;
    border: 1px gray solid;
    border-radius: 6px;
}

input:last-of-type {
    margin-left: 45px;
    border: 1px gray solid;
    border-radius: 4px;
    margin-top: 3px;
    margin-bottom: 4px;
    padding: 5px 10px;
    padding-bottom: 5px;
}

table {
    background-color: #333333;
    margin-bottom: 10px;
}

tr:first-of-type {
    line-height: 25px;
}

td {
    padding: 3px 10px 3px 10px;
}

td a {
    color: #f9fcff;
    text-decoration: none;
}

td a:hover {
    text-decoration: underline;
}

.deftbl {
    line-height: 25px;
}

.deftbl th, .deftbl td, .deftbl tr {
    border: 1px #606060 solid;
}

td.seprow {
    background-color: #333333;
}

th {
    background-color: #333333;
}

th.resultheader {
    background-color: #333333;
    padding: 0px 10px 0px 10px;
}

td.resulttotal {
    background-color: #333333;
    padding: 0px 8px 0px 8px;
}

td.resultc3 {
    border: 1px solid black;
    padding: 0px 8px 0px 8px;
}

tr.testacc {
    color: black;
    line-height: 25px;
}

.resulttable th, .resulttable td, .resulttable tr {
    border: 1px #606060 solid;
}

table.deftbl tr:nth-child(even) {
    background-color: #333333;
}

table.deftbl tr:nth-child(odd) {
    background-color: #333333;
}

td.goodscore a, td.badscore a, td.avgscore10 a, td.avgscore30 a, td.avgscore50 a, td.avgscore70 a, td.avgscore90 a {
    color: black;
}

a {
    color: #00a9ff;
}

.ets {
    color: #f9fcff;
}

div.err div.ets {
    background-color: #a52b2b;
}

div.el div:hover {
    background-color: initial;
}

code {
    font-family: Trebuchet MS, sans-serif;
}

#editframe {
    border: 1px #585858 solid;
}

textarea {
    color: #f9fcff;
    background-color: #303030;
}

#linenums {
    background-color: #121212;
}

#linenumbers {
    background-color: #333333;
    margin-top: 10px;
    border: none;
}

.lineno {
    color: #f9fcff;
}

#commit {
    height: auto;
    border: 1px gray solid;
    border-radius: 6px;
    margin-right: 10px;
    padding: 10px;
    background-color: #eaeaea;
}

#addbtn {
    padding: 5px;
    border: 1px gray solid;
    border-radius: 4px;
    background-color: #eaeaea;
}

#codewindow {
    border-left: none;
    padding-left: 25px;
    background-color: #333333;
    color: #f9fcff;
    margin-top: 10px;
    border: none;
}

#filelist li {
    background-color: #364280;
}

#backmenu {
    margin-top: 6px;
	border: 1px gray solid;
    border-radius: 6px;
}

#backmenu a {
	background-color: #ffffff;   
}

.prep {
    color: #ffab54;
}

.cmt {
    color: #43ab43;
}

.kwd {
    color: #00a9ff;
}

.norm {
    color: #f9fcff;
}

.str {
    color: #ff00f7;
}

.num {
    color: #b9ff6d;
}

tr.testfail {
    background-color: #ff5d5d;
    line-height: 25px;
    color: black;
}

tr.testfail a {
    color: black;
}
}
