/*
 * Amazon.de Eyesaver
 *
 * Author      : Jadx
 * Updated     : Dezember 16th, 2009
 * Description : Modifies the colors and images on Amazon.de
 * Usage       : Use with the Stylish Firefox extension (http://userstyles.org/) or copy to your Firefox userContent.css file
 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.amazon.de") {

 body
  {
    background: #000 !important;
    position: relative !important;
    top: 0px !important;   
    width: 99% !important;
    margin: 0 Auto  !important;
  }

/* Linkfarbe */
a:link, a:visited, a:hover, a:active, .link, a font[color="#eee"] 

{
  color: #A9A9A9 !important;
}

/* Textfarbe */
div, td 

{ 
  color: #B0C4DE !important; 
}

.ratingWithoutPrimeImage
{background: #000 !important;}

.dataColumn
{background: #000 !important;}

.imageColumn
{background: #000 !important;}

table
{background: #000 !important;}

.srSprite
{background: #000 !important;}

.navSprite
{background: #000 !important;}

}
