@-moz-document domain("phase-6.de") {
:root {
    --color: /*[[color]]*/;
    --color2: /*[[color2]]*/;
}

body.darkTheme {
    --highlighted-background-color: var(--color);
    --background-sidebar-current: var(--color);
    --highlighted-text-color: var(--color);
    --highlighted-orange-white-text-color: var(--color);
    --background-darkorange-darkorange-specific: var(--color);
	--highlighted-border-color: var(--color); 
    --color-999-darkorange: var(--color);

    
    --main-color: var(--color);
    --secondary-color: var(--color2);
}

}
