@namespace url(http://www.w3.org/1999/xhtml);

/* Cette feuille de style a Ã©tÃ© crÃ©Ã©e par Flobesst pour le forum de JVF.com (www.jeuxvideo-flash.com).
Un petit tour sur www.bazar-info.net en guise de remerciement, Ã§a me fera plaisir ;)

Version: 1.0 */

@-moz-document domain("www.jeuxvideo-flash.com") {

/* --------------------------------- */
/* PARTIE COMMUNE A TOUTES LES PAGES */
/* --------------------------------- */

/* Permet d'avoir un design extensible (qui prend toute la fenÃªtre) */
body table { width: 100% !important; }


/* Fond d'Ã©cran et couleur du fond de page */
html {  }
body { background-color: transparent !important; }


/* Supprime le contour blanc de la page */
#mesdiscussions { border: none !important; }


/* Agrandis la taille des liens de navigation */
.arbo, .arbo a { background-color: white !important;
font-size: 12px !important; }


/* Modifie les onglets */
table.none tbody tr td { margin-bottom: -115px !important; }

/* ---------------------------------------------- */
/* PARTIE CONCERNANT LA LISTE DES FORUMS / SUJETS */
/* ---------------------------------------------- */

/* Color les cases de la liste des catÃ©gories */
td.catCase1bis, td.catCase3 { background-color: white !important; }
td.catCase0, td.catCase2 { background-color: #e2e8fe !important; }
td.catCase0, td.catCase1bis, td.catCase2, td.catCase3 { border: none !important; }


/* Colore les cases de la liste de sujets */
td.sujetCase1, td.sujetCase6, td.sujetCase9 { background-color: #e2e8fe !important; }
td.sujetCase2, td.sujetCase3, td.sujetCase4, td.sujetCase5, td.sujetCase7, td.sujetCase8, td.sujetCase10 { background-color: white !important; }
td.sujetCase1, td.sujetCase2, td.sujetCase3, td.sujetCase4, td.sujetCase5, td.sujetCase6, td.sujetCase7, td.sujetCase8, td.sujetCase9 { border: none !important; }

/* --------------------------------------- */
/* PARTIE CONCERNANT LE CONTENU DES SUJETS */
/* --------------------------------------- */

/* Agence le haut des tableaux */
table.main { margin-bottom: 5px !important;
border: 1px solid #999 !important; }
tr.cBackHeader td.padding, tr.cBackHeader th { background-image: none !important; }
tr.cBackHeader th, tr.cBackHeader th { background-color: #afcdff !important;
border-top: 1px solid white !important;
border-left: 1px solid white !important;
border-right: none !important;
border-bottom: none !important;
color: white !important; }


/* Agrandis la taille du titre du sujet */
.s1Ext h1 { width: 30% !important;
font-size: 20px !important; }


/* Fais ressortir et augmente la taille des liens "Messages privÃ©s" et "DÃ©connexion, Profil, Liste de contacts, Aide" */
.menu, .menu a, .left .left a { font-size: 12px !important; }
.menu, .left .left { padding: 3px !important;
background-color: white !important;
border: 1px outset black !important; }


/* Agence les cadres de profil/message */
table.messagetable { margin-bottom: 5px !important;
border: 5px solid #e2e8fe !important; }


/* Colore la case du profil */
table.messagetable tbody tr.message td.messCase1 { background-color: #e2e8fe !important;
border-top: none !important;
border-left: none !important;
border-right: none !important;
border-bottom: none !important;
font-size: 11px !important; }


/* Colore la case du message */
table.messagetable tbody tr.message td.messCase2 { background-color: white !important;
border: 1px solid #dadada !important; }


/* Colore la barre de sÃ©paration de la barre d'options et du message */
.toolbar { border-bottom-color: #999 !important; }


/* Colore le texte "PostÃ© le xx-xx-xxxx Ã  xx:xx:xx" */
.toolbar .left { color: #999 !important; }


/* Augmente la taille de la rÃ©ponse rapide */
.reponserapide { height: 200px !important;
width: 100% !important; }

/* ------------------------------- */
/* PARTIE CONCERNANT LA MODERATION */
/* ------------------------------- */

/* Agrandis la taille d'Ã©criture dans le tableau de modÃ©ration en haut et le colore */
.headerModeration tbody tr th, .headerModeration tbody tr td { font-size: 12px !important; }
.headerModeration tbody tr td { background-color: white !important; }



}
