css:
@-moz-document domain("crunchyroll.com") {

*{
background-color:rgb(0,0,0) !important;
color:rgb(210,210,210);}

.welcome-simulcasts-release{
color:rgb(219,135,0)!important}

#welcome_left{
border-right:2px solid orange!important;
}

.welcome-more{
top:-0px!important;
}

#header_country_flag{
margin-left:-20px!important;
background-color:transparent!important;
}

.welcome-pulse-title{
padding:2px!important;
background-color:#000!important;
}

.welcome-pulse-title:hover{
background-color:#222!important;
}

.header-userpanel-block{
-moz-border-radius:10px!important;
background-color:transparent!important;
background-image:url('http://i46.tinypic.com/qxqec3.png')!important;
border: 1px solid orange!important;
margin-left:3px!important;
font-weight:bold!important;
opacity:0.9;
}
.header-userpanel-block:hover{
-moz-border-radius:10px!important;
background-image:url('http://i48.tinypic.com/63rsao.png')!important;
border: 1px solid orange!important;
margin-left:3px!important;
opacity:1!important;
text-shadow:1px 0px 1px #000, 0 -1px 1px #000, 0px 1px 1px #000, -1px 0px 1px #000!important;
}

h1{border-bottom:2px solid orange!important;}
h2{border-bottom:2px solid orange!important;}
h3{border-bottom:2px solid orange!important;}
.welcome-countdown-day{border-bottom:2px solid orange!important;}
.welcome-news-item clearfix{border:2px solid orange!important;}

.welcome-news-comments, .forum_main-topic-desc-numcomments{
color:#fff!important;
background-image:url('http://i48.tinypic.com/2415x7t.png')!important;
background-repeat:repeat-y!important;
background-position:0px 0px!important;
border: 2px solid #db8700!important;
-moz-border-radius:10px!important;
-moz-border-radius-bottomright:0px!important;
height:32px!important;
width:auto!important;
min-width:44px!important;
max-width:70px!important;
text-shadow:1px 0px 1px #000, 0 -1px 1px #000, 0px 1px 1px #000, -1px 0px 1px #000!important;
}

.welcome-supporter a{
color:#fff!important;
background-image:url('http://i46.tinypic.com/qxqec3.png')!important;
background-repeat:repeat-x!important;
background-position:0px 22px!important;
background-color:#b87000!important;
border: 1px solid #b87000!important;
}

.right{
color:#fff!important;
background-image:url('http://i46.tinypic.com/qxqec3.png')!important;
background-repeat:repeat-x!important;
background-position:0px 22px!important;
background-color:#b87000!important;
}

.header-menubar-item, .header_menubar{
color:#fff!important;
background-repeat:repeat-x!important;
background-position:0px 0px!important;
background-color:transparent!important;
}

.header-menubar-item a{
background-color:#000!important;
}

#header_menubar{
top:8px!important;
margin-bottom:6px!important;
-moz-border-radius:10px!important;
background-image:url('http://i45.tinypic.com/ckfg4.png')!important;
background-position:0px 8px!important;
background-repeat:repeat-x!important;
background-color:#000!important;
height:36px!important;
border:2px solid orange!important;
}

.header-menubar-item a{
background-color:transparent!important;
border:1px solid transparent!important;
border:3px solid transparent!important;
text-shadow:0px 0px 10px #fff!important;
}

.header-menubar-item a:hover{
height:26px!important;
color:#fff!important;
background-color:orange!important;
text-shadow:1px 0px 1px #000, 0 -1px 1px #000, 0px 1px 1px #000, -1px 0px 1px #000!important;
}

a#logo{
background-image: url('http://i50.tinypic.com/2466k3d.gif')!important;
}

.paginator-lite-selected{
padding:4px 7px 4px 7px!important;
font-weight:bold!important;
border:2px solid orange!important;
color:#fff!important;
background-color:#fff!important;
text-shadow:2px 0px 1px #000, 0 -1px 1px #000, 0px 1px 1px #000, -1px 0px 1px #000, 0px 0px 5px #000, 0px 0px 5px #000!important;
}

.paginator-lite-disabled{
border:2px solid #555!important;
color:#fff!important;
opacity:0.3;
}

.paginator-lite{
border:2px solid #555!important;
}


.paginator-lite:hover{
background-color:#999!important;
}

#toolbar{
border-top:1px solid orange!important;
background-color:rgb(219,135,0)!important;
background-image:url('http://i46.tinypic.com/qxqec3.png')!important;
z-index:1000!important;
}

.toolbar-item{
border-right:1px solid orange!important;
background-color:rgb(219,135,0)!important;
background-image:url('http://i46.tinypic.com/qxqec3.png')!important;
}

.toolbar-item:hover{
color:#000!important;
border-right:1px solid orange!important;
background-color:rgb(219,135,0)!important;
background-image:url('http://i48.tinypic.com/63rsao.png')!important;
}

.toolbar-item-hover{
color:#000!important;
border-right:1px solid orange!important;
background-color:rgb(219,135,0)!important;
background-image:url('http://i48.tinypic.com/63rsao.png')!important;
}

.toolbar-item-right{
border-left:0px solid orange!important;
}

.footer{
background-color:#444!important;
}

#footer_menu{
border-top:2px solid orange!important;
}

.footer-column h6{
color:orange!important;
}

.promo-details{
background-image:url('http://i49.tinypic.com/2r2qayx.png')!important;
}

.promo-main-header{
margin:15px!important;
background-color:#000!important;
background-image:url('http://i47.tinypic.com/1f9unt.png')!important;
background-position:0px 10px!important;
background-repeat:no-repeat;
height:85px!important;
width:920px!important;
}

.message-container{
opacity:0.5!important;
}
.message-container:hover{
opacity:1!important;
}

.promo-paypal-logo{
opacity:0;
}

h1, h2, h3, .promo-pick-plan{
background-color:transparent!important;
color:#fff!important;
border-bottom:2px solid #db8700!important;
text-shadow:#000 0px 1px 4px !important;
}

.promo-links{
display:none!important;
}

a:link{
color:#fff!important; background-color:transparent!important;
background-color:transparent!important;
}

.strong, .countdown-timer-days, .countdown-timer-hours, .countdown-timer-minutes, .countdown-timer-seconds{
color:#db8700!important
}

a:visited, .library-tab-count, .helpful, .dimtext, .spam, .bold, .img {
background-color:transparent!important;
color:#fff!important;}
}

.news-feed-body clearfix{
z-index:0!important;
}

.dim{
padding-bottom:3px!important;
background-color:rgb(219,135,0)!important;
background-image:url('http://i46.tinypic.com/qxqec3.png')!important;
background-position:0px -2px!important;
}

.topbar-nav-sub{
-moz-border-radius-bottomleft:10px!important;
-moz-border-radius-bottomright:10px!important;
width:911px!important;
padding-left:20px!important;
padding-bottom:3px!important;
background-color:rgb(219,135,0)!important;
background-image:url('http://i46.tinypic.com/qxqec3.png')!important;
background-position:0px 0px!important;
margin-left:8px!important;
}

.tab{
padding-left:0px!important;
background-color:rgb(50,50,50)!important;
background-image:none!important;
}

.search-tab-widget, .tab a{
border:0px solid #fff!important;
}

.highlight{
padding-bottom:3px!important;
background-color:orange!important;
text-shadow:1px 0px 1px #000, 0 -1px 1px #000, 0px 1px 1px #000, -1px 0px 1px #000!important;
}

#template_action_button a, .action-button {
background-color:#000!important;
}

#template_action_button a:hover, .action-button:hover {
background-color:orange!important;
}

/*FORUM*/

.forum_main-topic-desc{
-moz-border-radius:10px!important;
background-image:none!important;
background-color:orange!important;
}

.forum_main-topic-desc-text, .forum_main-topic-desc-author, .forum_main-topic-desc-title{
color:#333!important;
background-color:orange!important;
width:160px!important;
}

/*AD*/

div#template_skin_leaderboard{
display:none;
}
