css:
@-moz-document domain("whatsapp.com") {
#app > div > div:first-of-type {
    background: url(https://i.ibb.co/7JnyRgQ/full.png);
    background-size: cover;
}
#app > div > div:first-of-type:after {
    height: 0px;
}

/*  -------------------------------------------------  */
/*  -----------------   LEFT SIDE   -----------------  */
/*  -------------------------------------------------  */
#side {
    background-color: rgba(1,0,0,0);
}
#side > header {
    display: none;
}
#side > span {
    display: none;
}
#side > div:first-of-type {
    display: none;
}
#pane-side > div > div > div > div > div > div {
    background-color: rgba(2,0,0,0);
}
#pane-side > div > div > div > div > div > div:after {
    border-top: 0px;
}
#pane-side {
    background-color: rgba(3,0,0,0);
    padding-top: 80px;
    overflow: hidden;
}
#pane-side > div > div > div > div > div > div > div:first-child {
    display: none;
}
#pane-side > div > div > div > div > div > div > div:last-child {
    border-top: 0px;
    opacity: 0;
}
#pane-side > div > div > div > div > div > div > div:last-child:hover {
    opacity: 0.8;
}
#pane-side > div > div > div > div > div > div > div:last-child > div {
    margin-left: 60px;
    justify-content: flex-end;
}
#pane-side > div > div > div > div > div > div > div:last-child > div > div {
    flex-grow: 0;
}
#pane-side > div > div > div > div > div > div > div:last-child > div > div > span {
    color: #fff;
}

/*  -------------------------------------------------  */
/*  -------------------   MAIN   --------------------  */
/*  -------------------------------------------------  */
/*  -------------------   HEADER   --------------------  */
#main > header > div:first-child {
    display: none;
}
#main > header > div:last-child {
    display: none;
}
#main > header > div > div:nth-child(2) {
    display: none;
}
#main > header {
    height: 30px;
    background-color: rgba(10,0,0,0);
    border-left: none;
}
#main > header:after {
    border-bottom: none;
    background-color: rgba(10,0,0,0);
}
#main {
    background-color: rgba(255, 255, 4, 0);
}
#main > div {
    background-image: none;
}
#main > header > div > div > div > span {
    margin-top: 13px;
    margin-left: 174px;
    color: #d5d5d5;
    font-style: italic;
    max-width: 125px;
    text-overflow: ellipsis;
}
/*  -------------------   MESSAGES   --------------------  */
#main > div > div > div:first-of-type {
    display: none;
}
#main > div > div > div:last-of-type {
    border-left: none;
}
#main > div > div > div > div > div { /* .massage-in and .massage-out */
    margin-right: 11%;
    padding-right: 20px;
}
.message-in > div > span {
    display: none;
}
.message-out > div > span {
    display: none;
}
#main > div > div > div > div > div > div > div {
    background-color: #222;
}
#main > div > div > div > div > div {
    opacity: 0;
}
#main > div > div > div > div > div:hover {
    opacity: 1;
}
#main > div > div > div > div > div > div > div > span {
    display: none;
}
#main > div > div > div > div > div > div > div > div > div > div > span > span {
    color: #fff;
}
#main > div > div > div > div > div > div {
    background-color: rgba(5,0,0,0);
}
/*  -------------------   FOOTER   --------------------  */
#main > footer {
    background-color: rgba(6,0,0,0);
    min-height: 120px;
    margin-top: 20px;
}
#main > footer > div {
    background-color: rgba(7,0,0,0);
    border-left: 0px;
}
#main > footer > div > div > span > div > div:first-child {
    margin-left: 20px;
}
#main > footer > div > div > span > div > div:last-child > div:first-child {
    background-color: rgba(8,0,0,0);
    margin-left: 278px;
    border: 0px;
}
#main > footer > div > div > span > div > div:last-child > div:first-child > div > div:first-child {
    opacity: 0;
}
#main > footer > div > div > span > div > div:last-child > div:first-child > div > div:last-child {
    color: #fff;
}
#main > footer > div > div > span > div > div:last-child > div:last-child {
    display: none;
}


    
    
    
    
    
    
    
    

