css:
@-moz-document url-prefix("https://www.home-assistant.io/") {

/*---Var---*/
  :root
  {
	--home-assistant: /*[[home-assistant]]*/;
        --background-dark: /*[[background-dark]]*/;
	--background-dark2: /*[[background-dark2]]*/;
        --text-color-dark: /*[[text-color-dark]]*/;
	--border-color-dark: /*[[border-color-dark]]*/;
  }

body, html {
    background-color: var(--background-dark);
    color: var(--text-color-dark);
}

body>header {
    background-color: var(--background-dark2);
}
code {
    background-color: var(--background-dark2);
    border: 1px solid var(--border-color-dark);
}
pre[class*="language-"]>code {
    border-left: 10px solid var(--home-assistant);
    background-color: var(--background-dark2);
}
pre.highlight {
    background-color: var(--background-dark);
    border: 0px solid #fff!important;
}
div.config-vars .config-vars-description-and-children {
    color: var(--text-color-dark);
}
div.config-vars .config-vars-label-name {
    font-weight: bold;
    font-size: 16px;
    color: #666666;
}
p.note::before, div.note::before {
    background-color: var(--background-dark2);
}
.material-card {
    background-color: var(--background-dark2);
}
body>footer, .page-content>footer {
    background-color: var(--background-dark2);
}
.hass-option-cards .option-card {
    background-color: var(--home-assistant);
}
div.details-block .details-block-item {
    background-color: var(--background-dark2);
}
article.post>table, article.post>.entry-content>table, article.page>table, article.page>.entry-content>table, article.listing>table, article.listing>.entry-content>table {
    background-color: var(--background-dark2);
}
#components-page .filter-button-group .btn {
    color: var(--text-color-dark);
}
#components-page .component-search input {
    background-color: var(--home-assistant);
    border-color: #7c7c7c #c3c3c3 #ddd;
}
}
