css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.lokalisten.de"), domain("www.lokalisten.at"), domain("www.lokalisten.ch"), domain("www.lokalisten.com") {
*{margin:0;padding:0;border:0 none;}
body{background:#669900 !important ;color:black !important;font:100.1% "Verdana","Lucida Sans","Lucida Sans Unicode","Lucida Grande",sans-serif;padding:6px 0 12px 0;}
div.alertMessage p a,div.errorMessage p a,div.message p a{color:#333!important;text-decoration:underline;}
div.alertMessage p a{font-weight:bold!important;}
div.headerSearch input.searchbt{width:190px;padding:2px 0 0 4px;height:18px!important;border:0 none;color:#999;font-size:11px;position:absolute;right:20px;top:0;}
div.mainNav{background:#669900 !important;}
ul.mainMenu{float:left;font-size:12px;background:#222;}
ul.mainMenu ul.dropDown{float:none;width:160px;position:absolute;z-index:10000!important;top:33px;left:0;font-size:12px;background:#fff!important;border:#ccc 1px solid;padding:6px 0;margin:0;}
ul.mainMenu ul.dropDown li a.active,ul.mainMenu ul.dropDown li.selected,ul.mainMenu ul.dropDown li a:hover,ul.mainMenu ul.dropDown li:hover,ul.mainMenu ul.dropDown li span:active,ul.mainMenu ul.dropDown li.selected,ul.mainMenu ul.dropDown li span:hover{background:#eaeaea!important;color:#444;}
div.footer{text-align:center;background:#9f9f9f !important;}
p.partnerFooter{padding:9px 0 6px 0;background:black !important; ;font-size:10px;color:white !important;}
p.subFooter{font-size:9px;padding:3px 12px 0 12px;color:#ccc;background:#9f9f9f !important;}
p.dirFooter{font-size:11px;padding:3px 12px 12px 12px;color:#ccc;background:#9f9f9f !important;}
p.legalNote{color:#ccc;font-size:9px;text-align:center;background:#9f9f9f !important;}
.moveBoxHeader {background:#9f9f9f !important;}
.moveBoxSub {background:#cfcfcf !important;}
.friendAktivityHome {background:#dfdfdf !important;}
.teaserList {background:#dfdfdf !important;}
.onlineList {background:#dfdfdf !important;}
.statusBox {background:#dfdfdf !important;}
.statusBar {background:#aaaaaa !important;}
.bx_home {background:#88bb00 !important;}
.salutationBar {background:#afafaf !important;}
.tabHeader {background:#9f9f9f !important;}
.buttonBar {background:#336600 !important;}
.simpleNote {background:#ff5555 !important;}
.mainCont {background:#9f9f9f !important;}
.clear {background:#9f9f9f !important;}
div.bx_home div.notifyBox {
	display:none !important;
}
div.bx_home div.fixedBox {
	display:none !important;
}
div.wrapper div.finCont {
	display:none !important;
}

textarea, input[type="text"], input[type="password"], input[type="input"], .appMenu, .mainMenu {-moz-border-radius:10px;border-left:#444 1px solid!important;border-top:#3f3f3f 1px solid!important;}
.appMenu {background:#336600 !important;}
.mainMenu {background:#336600 !important;}
.w_40 {background:#336600 !important;}
.textLink {color:#000000 !important;}
.status {background:#99cc00 !important;}
.userpic {background:#99cc00 !important;}
.authorPic {background:#99cc00 !important;}
.from{background:#99cc00 !important;}
.answered {background:#99cc00 !important;}
.subject {background:#99cc00 !important;}
.action {background:#99cc00 !important;}
.userList {background:#cfcfcf !important;}
.normalClass {background:#dfdfdf !important;}
.simpleBoxInner {background:#cfcfcf !important;}
.simpleBoxInnerCont {background:#dfdfdf !important;}
.resultBox {background:#cfcfcf !important;}
.resultBoxCont {background:#dfdfdf !important;}
.miniProfileWrapper {background:#cfcfcf !important;}
.sortColumn2 {background:#dfdfdf !important;}
.statusContent {background:#669900 !important;color:#0000af !important}
.sortColumn1 {background:#99bb00 !important;}
.bx_profile {background:#669900 !important;}
.static_wrap {background:#afafaf !important;}
.sortColumn2 {background:#afafaf !important;}
.friendTag {background:#336600 !important;}
.alphaSort {background:#336600 !important;}
.pg {background:#99bb00 !important;}
.resultList {background:#99bb00 !important;}
.miniTabProfile {background:#afafaf !important;}
.breadCrumb {background:#336600 !important;}
.galleryList {background:#bfbfbf !important;}
.bx_movebox {background:#cfcfcf !important;}
.bx_groups {background:#669900 !important;}
.selected {-moz-border-radius:10px;border-left:#444 1px solid!important;border-top:#3f3f3f 1px solid!important;}
}
