css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain('socialcast.com') {


html, body {
  width: 100% !important;
  height: 100% !important;
  min-height: 100% !important;
  max-height: 100% !important;
  margin: 0 !important;
  padding: 0 !important;
}


body #main_container {
  /*
  box-shadow: 0 5px 30px rgba(0,0,0,0.7);
  -moz-box-shadow: 0 5px 30px rgba(0,0,0,0.7);
  -webkit-box-shadow: 0 5px 30px rgba(0,0,0,0.7);
  */
  position: fixed !important;
  left: 0 !important;
  top: 0 !important;
  width: 100% !important;
  height: 100% !important;
  min-height: 100% !important;
  max-height: 100% !important;
  padding: 0 !important;
  margin: 0 !important;
  border: none !important;
  border-radius: 0 !important;
  -moz-border-radius: 0 !important;
  -webkit-border-radius: 0 !important;
  overflow: hidden !important;
}


body #sidebar {
  position: absolute !important;
  display: block !important;
  left: 0 !important;
  top: 0 !important;
  overflow: auto !important;
  height: 100% !important;
  overflow-x: hidden !important;
}

body .primary_content {
  position: absolute !important;
  display: block !important;
  width: auto !important;
  height: 100% !important;
  min-height: 100% !important;
  max-height: 100% !important;
  padding: 0 5px !important;
  margin: 0 !important;
  left: 200px !important;
  top: 0 !important;
  /* margin: 0 200px 0 0 !important; */
  margin: 0 !important;
  right: 200px !important;
  overflow: auto !important;
  overflow-x: hidden !important;
}


body .secondary_content {
  /*
  float: right !important;
  clear: none !important;
  */
  position: absolute !important;
  display: block !important;
  right: 0 !important;
  top: 0 !important;
  width: 190px !important;
  padding: 5px !important;
  height: 100% !important;
  overflow: auto !important;
  overflow-x: hidden !important;
}

body .side_content {
  position: fixed !important;
  display: block !important;
  width: 190px !important;
  right: 0 !important;
  top: 0 !important;
  height: 100% !important;
  padding: 5px !important;
  overflow: auto !important;
  overflow-x: hidden !important;
}







.global_search .btn_search {
  display: none;
}


body .fdbk_tab_right, body #fdbk_tab {
  display: none !important;
}


}
