css:
/* i really want this to be global */
.col-sm-5 {
  width: 20%;
}
.col-sm-5 > h2{display: none;}
.col-sm-7{
  width: 80%;
}
.video-player1{
  margin-top: 1rem;
}
.chanels.channels-ul li{	
  display: flex;
  align-items:center;
  justify-content:space-between;
}
.chanels.channels-ul li label,
.chanels.channels-ul li i{
  margin: 0;
}
.chanels.channels-ul li label {
  font-size: 14px;
  flex:1;
}
.chanels.channels-ul li number {
  margin-right:auto;
}
.chanels.channels-ul li .number img{
	height: 2rem;
  width: 2rem;
}
